sys_script_include.script:
(function() {
  // API : generate_word
  if (input && input.action === 'generate_word') {
    try {
      var analysisId = input.analysis_id;

      // R√©cup√©rer les r√©sultats de l'analyse
      var gr = new GlideRecord('u_fha_results');
      if (gr.get(analysisId)) {
        var results = JSON.parse(gr.summary + '');

        // G√©n√©rer le contenu HTML du rapport
        var htmlContent = generateWordReport(results);

        // Convertir en document Word via REST API
        var fileName = 'Analyse_' + results.table + '_' + new GlideDateTime().getDisplayValue() + '.doc';

        // Cr√©er un document temporaire
        var sysId = createWordDocument(fileName, htmlContent);

        data.document_url = '/api/now/attachment/download?table=sys_attachment&id=' + sysId;
        data.success = true;
        data.message = 'Document g√©n√©r√© avec succ√®s';
      } else {
        data.success = false;
        data.message = 'Analyse non trouv√©e';
      }
    } catch (e) {
      data.success = false;
      data.message = 'Erreur : ' + e.toString();
    }
  }

  // Fonction pour g√©n√©rer le contenu HTML du rapport
  function generateWordReport(results) {
    var html = '';

    // En-t√™te HTML pour Word
    html += '<html xmlns:v="urn:schemas-microsoft-com:vml"' +
            'xmlns:o="urn:schemas-microsoft-com:office:office"' +
            'xmlns:w="urn:schemas-microsoft-com:office:word"' +
            'xmlns:m="http://schemas.microsoft.com/office/2004/12/omml"' +
            'xmlns="http://www.w3.org/TR/REC-html40">' +
            '<head>' +
            '<meta charset="utf-8">' +
            '<title>Analyse Foundation - ' + results.table + '</title>' +
            '<style>' +
            'body { font-family: Calibri, Arial, sans-serif; }' +
            'h1 { color: #0378BE; border-bottom: 2px solid #0378BE; }' +
            'h2 { color: #035B8E; }' +
            '.score { font-size: 36px; font-weight: bold; padding: 10px; border-radius: 5px; display: inline-block; }' +
            '.score-good { background-color: #dff0d8; color: #3c763d; }' +
            '.score-medium { background-color: #fcf8e3; color: #8a6d3b; }' +
            '.score-poor { background-color: #f2dede; color: #a94442; }' +
            'table { border-collapse: collapse; width: 100%; }' +
            'th { background-color: #0378BE; color: white; padding: 8px; text-align: left; }' +
            'td { border: 1px solid #ddd; padding: 8px; }' +
            '.priority-high { color: #d9534f; font-weight: bold; }' +
            '.priority-medium { color: #f0ad4e; }' +
            '.priority-low { color: #5bc0de; }' +
            '.section { margin-bottom: 30px; }' +
            '</style>' +
            '</head>' +
            '<body>';

    // En-t√™te du document
    html += '<div class="section">' +
            '<h1>üìä Rapport d\'Analyse Foundation</h1>' +
            '<p><strong>Table analys√©e :</strong> ' + results.table + '</p>' +
            '<p><strong>Date de l\'analyse :</strong> ' + results.timestamp + '</p>' +
            '</div>';

    // Score de sant√©
    var scoreClass = '';
    if (results.health_score >= 70) {
      scoreClass = 'score-good';
    } else if (results.health_score >= 40) {
      scoreClass = 'score-medium';
    } else {
      scoreClass = 'score-poor';
    }

    html += '<div class="section">' +
            '<h2>Score de Sant√© Global</h2>' +
            '<div class="score ' + scoreClass + '">' + results.health_score + '/100</div>' +
            '</div>';

    // R√©sum√© ex√©cutif
    html += '<div class="section">' +
            '<h2>R√©sum√© Ex√©cutif</h2>' +
            '<p>' + generateExecutiveSummary(results) + '</p>' +
            '</div>';

    // Tableau des champs custom
    if (results.findings && results.findings.custom_fields) {
      html += '<div class="section">' +
              '<h2>üìã Champs Personnalis√©s</h2>' +
              '<table>' +
              '<tr>' +
              '<th>Nom du champ</th>' +
              '<th>Label</th>' +
              '<th>Type</th>' +
              '<th>Taux de remplissage</th>' +
              '<th>Recommandation</th>' +
              '</tr>';

      results.findings.custom_fields.forEach(function(field) {
        html += '<tr>' +
                '<td><code>' + field.name + '</code></td>' +
                '<td>' + field.label + '</td>' +
                '<td>' + field.type + '</td>' +
                '<td>' + field.fill_rate + '%</td>' +
                '<td>' + (field.recommendation ? field.recommendation.action : 'Aucune') + '</td>' +
                '</tr>';
      });

      html += '</table>' +
              '<p><strong>Total :</strong> ' + results.findings.custom_fields.length + ' champs personnalis√©s d√©tect√©s</p>' +
              '</div>';
    }

    // Recommandations prioritaires
    if (results.recommendations) {
      html += '<div class="section">' +
              '<h2>üö® Recommandations Prioritaires</h2>' +
              '<table>' +
              '<tr>' +
              '<th>Priorit√©</th>' +
              '<th>Action</th>' +
              '<th>Champ/√âl√©ment</th>' +
              '<th>Raison</th>' +
              '<th>Effort estim√©</th>' +
              '</tr>';

      results.recommendations.forEach(function(rec) {
        var priorityClass = 'priority-' + rec.priority.toLowerCase();
        html += '<tr>' +
                '<td class="' + priorityClass + '">' + rec.priority + '</td>' +
                '<td>' + rec.action + '</td>' +
                '<td>' + rec.field + '</td>' +
                '<td>' + rec.reason + '</td>' +
                '<td>' + rec.effort + '</td>' +
                '</tr>';
      });

      html += '</table>' +
              '</div>';
    }

    // D√©pendances
    if (results.findings && results.findings.dependencies) {
      html += '<div class="section">' +
              '<h2>üîó D√©pendances</h2>' +
              '<p><strong>Business Rules :</strong> ' + 
              (results.findings.dependencies.business_rules || []).length + ' trouv√©es</p>' +
              '<p><strong>ACLs :</strong> ' + 
              (results.findings.dependencies.acls || []).length + ' trouv√©es</p>' +
              '<p><strong>UI Policies :</strong> ' + 
              (results.findings.dependencies.ui_policies || []).length + ' trouv√©es</p>' +
              '</div>';
    }

    // Conclusion
    html += '<div class="section">' +
            '<h2>Conclusion</h2>' +
            '<p>Cette analyse a √©t√© g√©n√©r√©e automatiquement par l\'outil Foundation Health Analyzer.</p>' +
            '<p>Pour toute question, contactez votre administrateur ServiceNow.</p>' +
            '</div>';

    html += '</body></html>';

    return html;
  }

  // Fonction pour g√©n√©rer le r√©sum√© ex√©cutif
  function generateExecutiveSummary(results) {
    var summary = '';

    if (results.health_score >= 70) {
      summary = 'La sant√© de la table est excellente. Peu d\'actions correctives sont n√©cessaires.';
    } else if (results.health_score >= 40) {
      summary = 'La table pr√©sente des am√©liorations potentielles. Quelques actions correctives sont recommand√©es.';
    } else {
      summary = 'Attention : Cette table n√©cessite une r√©vision urgente. De nombreuses am√©liorations sont n√©cessaires.';
    }

    if (results.findings && results.findings.custom_fields) {
      var customCount = results.findings.custom_fields.length;
      summary += ' ' + customCount + ' champ(s) personnalis√©(s) ont √©t√© d√©tect√©(s).';
    }

    if (results.recommendations) {
      var highPriority = results.recommendations.filter(function(r) {
        return r.priority === 'HIGH';
      }).length;

      if (highPriority > 0) {
        summary += ' ' + highPriority + ' recommandation(s) haute priorit√© n√©cessite(nt) une attention imm√©diate.';
      }
    }

    return summary;
  }

  // Fonction pour cr√©er le document Word
  function createWordDocument(fileName, content) {
    // Cr√©er un enregistrement d'attachment
    var gr = new GlideRecord('sys_attachment');
    gr.initialize();
    gr.table_name = 'u_fha_results';
    gr.table_sys_id = input.analysis_id;
    gr.file_name = fileName;
    gr.content_type = 'application/msword';

    // Convertir le HTML en bytes pour l'attachment
    var base64Content = Packages.java.util.Base64.getEncoder().encodeToString(
      String(content).getBytes('UTF-8')
    );

    gr.insert(); // Insert d'abord pour avoir un sys_id

    // Maintenant ajouter le contenu dans sys_attachment_doc
    var grDoc = new GlideRecord('sys_attachment_doc');
    grDoc.initialize();
    grDoc.sys_attachment = gr.sys_id + '';
    grDoc.data = base64Content;
    grDoc.compressed = false;
    grDoc.insert();

    return gr.sys_id + '';
  }
})();
