sp_header_footer.client_script:
api.controller = function($scope, $location, $window) {
    var c = this;

    // Menu items
    c.menuItems = [
        {
            label: 'Dashboard',
            page: 'fha_homepage',
            icon: 'fa-tachometer-alt',
            active: false
        },
        {
            label: 'Results',
            page: 'fha_analysis_results',
            icon: 'fa-chart-bar',
            active: false
        }
    ];

    // Set active menu item based on current page
    c.$onInit = function() {
        var currentPage = $location.search().id || 'fha_homepage';
        c.menuItems.forEach(function(item) {
            item.active = (item.page === currentPage);
        });
    };

    // Navigate to page
    c.navigateTo = function(page) {
        $window.location.href = '/fha?id=' + page;
    };

    // Check if menu item is active
    c.isActive = function(item) {
        var currentPage = $location.search().id || 'fha_homepage';
        return item.page === currentPage;
    };

    // Toggle mobile menu
    c.mobileMenuOpen = false;
    c.toggleMobileMenu = function() {
        c.mobileMenuOpen = !c.mobileMenuOpen;
    };
};

sp_header_footer.script:
(function() {
    // Get current user info
    data.userName = gs.getUserDisplayName();
    data.userInitials = '';

    if (data.userName) {
        var nameParts = data.userName.split(' ');
        if (nameParts.length >= 2) {
            data.userInitials = nameParts[0].charAt(0) + nameParts[nameParts.length - 1].charAt(0);
        } else if (nameParts.length === 1) {
            data.userInitials = nameParts[0].charAt(0);
        }
        data.userInitials = data.userInitials.toUpperCase();
    }

    // Get portal info
    data.portalTitle = 'Foundation Health Analyzer';
    data.portalSubtitle = 'ServiceNow Analysis Tool';
})();
