sp_widget.client_script:
function($scope, spUtil, $location, $window) {
  var c = this;

    // Data from server
    c.analyses = c.data.analyses || [];
    c.totalCount = c.data.totalCount || 0;
    c.portalSuffix = c.data.portalSuffix || 'fha';

    // Pagination
    c.currentPage = 1;
    c.pageSize = 20;
    c.totalPages = Math.ceil(c.totalCount / c.pageSize);

    // Filters
    c.filters = {
        search: '',
        status: 'all',
        scoreRange: 'all'
    };

    // Sort
    c.sortField = 'sys_created_on';
    c.sortDirection = 'desc';

    // Get health score class
    c.getHealthScoreClass = function(score) {
        if (score >= 70) return 'good';
        if (score >= 40) return 'medium';
        return 'poor';
    };

    // Get health score label
    c.getHealthScoreLabel = function(score) {
        if (score >= 70) return 'Good';
        if (score >= 40) return 'Needs Attention';
        return 'Critical';
    };

    // View analysis detail
    c.viewDetail = function(analysis) {
        $window.location.href = '/' + c.portalSuffix + '?id=fha_analysis_detail&sys_id=' + analysis.sys_id;
    };

    // Load more analyses
    c.loadMore = function() {
        if (c.currentPage < c.totalPages) {
            c.currentPage++;
            c.loadAnalyses();
        }
    };

    // Load analyses from server
    c.loadAnalyses = function() {
        c.loading = true;
        c.server.get({
            action: 'load_analyses',
            page: c.currentPage,
            pageSize: c.pageSize,
            search: c.filters.search,
            status: c.filters.status,
            scoreRange: c.filters.scoreRange,
            sortField: c.sortField,
            sortDirection: c.sortDirection
        }).then(function(response) {
            c.loading = false;
            if (response.data.success) {
                if (c.currentPage === 1) {
                    c.analyses = response.data.analyses;
                } else {
                    c.analyses = c.analyses.concat(response.data.analyses);
                }
                c.totalCount = response.data.totalCount;
                c.totalPages = Math.ceil(c.totalCount / c.pageSize);
            }
        });
    };

    // Apply filters
    c.applyFilters = function() {
        c.currentPage = 1;
        c.loadAnalyses();
    };

    // Reset filters
    c.resetFilters = function() {
        c.filters = {
            search: '',
            status: 'all',
            scoreRange: 'all'
        };
        c.currentPage = 1;
        c.loadAnalyses();
    };

    // Sort by field
    c.sortBy = function(field) {
        if (c.sortField === field) {
            c.sortDirection = c.sortDirection === 'asc' ? 'desc' : 'asc';
        } else {
            c.sortField = field;
            c.sortDirection = 'desc';
        }
        c.currentPage = 1;
        c.loadAnalyses();
    };

    // Get sort icon
    c.getSortIcon = function(field) {
        if (c.sortField !== field) return 'fa-sort';
        return c.sortDirection === 'asc' ? 'fa-sort-up' : 'fa-sort-down';
    };

    // Format date
    c.formatDate = function(dateStr) {
        if (!dateStr) return '';
        var date = new Date(dateStr);
        return date.toLocaleDateString() + ' ' + date.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});
    };

    // Go back to dashboard
    c.goToDashboard = function() {
        $window.location.href = '/' + c.portalSuffix + '?id=fha_homepage';
    };

    // Delete analysis
    c.deleteAnalysis = function(analysis, $event) {
        $event.stopPropagation();
        if (confirm('Are you sure you want to delete this analysis?')) {
            c.server.get({
                action: 'delete_analysis',
                sys_id: analysis.sys_id
            }).then(function(response) {
                if (response.data.success) {
                    c.loadAnalyses();
                }
            });
        }
    };
}

sp_widget.script:
(function() {
    // Get portal suffix
    data.portalSuffix = $sp.getPortalRecord() ? $sp.getPortalRecord().getValue('url_suffix') : 'fha';

    // Load analyses
    data.analyses = [];
    data.totalCount = 0;

    var page = (input && input.page) || 1;
    var pageSize = (input && input.pageSize) || 20;
    var search = (input && input.search) || '';
    var status = (input && input.status) || 'all';
    var sortField = (input && input.sortField) || 'sys_created_on';
    var sortDirection = (input && input.sortDirection) || 'desc';

    // Handle actions
    if (input && input.action === 'delete_analysis') {
        var delGr = new GlideRecord('x_1310794_founda_0_results');
        if (delGr.get(input.sys_id)) {
            delGr.deleteRecord();
            data.success = true;
        }
        return;
    }

    // Build query
    var gr = new GlideRecord('x_1310794_founda_0_results');

    // Apply filters
    if (status !== 'all') {
        gr.addQuery('state', status);
    }

    if (search) {
        gr.addQuery('number', 'CONTAINS', search);
    }

    // Get total count
    var countGr = new GlideAggregate('x_1310794_founda_0_results');
    if (status !== 'all') {
        countGr.addQuery('state', status);
    }
    if (search) {
        countGr.addQuery('number', 'CONTAINS', search);
    }
    countGr.addAggregate('COUNT');
    countGr.query();
    if (countGr.next()) {
        data.totalCount = parseInt(countGr.getAggregate('COUNT'), 10);
    }

    // Apply sorting
    if (sortDirection === 'desc') {
        gr.orderByDesc(sortField);
    } else {
        gr.orderBy(sortField);
    }

    // Apply pagination
    gr.chooseWindow((page - 1) * pageSize, page * pageSize);
    gr.query();

    while (gr.next()) {
        var issues = [];
        var highCount = 0;
        var mediumCount = 0;
        var lowCount = 0;
        var tableDisplayName = '';
        var tableName = '';

        try {
            var detailObj = JSON.parse(gr.getValue('details') || '{}');
            issues = detailObj.issues || [];
            issues.forEach(function(issue) {
                if (issue.severity === 'high') highCount++;
                else if (issue.severity === 'medium') mediumCount++;
                else if (issue.severity === 'low') lowCount++;
            });
            tableDisplayName = (detailObj.config && (detailObj.config.table_label || detailObj.config.table_name)) || '';
            tableName = (detailObj.config && detailObj.config.table_name) || '';
        } catch(e) {
            // ignore parse errors
        }

        data.analyses.push({
            sys_id: gr.getUniqueValue(),
            number: gr.getValue('number') || '',
            table_name: tableName,
            table_label: tableDisplayName || 'N/A',
            health_score: calculateScore(issues),
            issue_count: issues.length,
            high_count: highCount,
            medium_count: mediumCount,
            low_count: lowCount,
            status: gr.getValue('state') || '',
            created_on: gr.getValue('sys_created_on') || '',
            created_by: gr.getDisplayValue('sys_created_by') || ''
        });
    }

    // Calculate stats for header
    data.stats = {
        total: data.totalCount,
        avgScore: 0,
        goodCount: 0,
        criticalCount: 0
    };

    if (data.analyses.length > 0) {
        var totalScore = 0;
        data.analyses.forEach(function(a) {
            totalScore += a.health_score;
            if (a.health_score >= 70) data.stats.goodCount++;
            if (a.health_score < 40) data.stats.criticalCount++;
        });
        data.stats.avgScore = Math.round(totalScore / data.analyses.length);
    }

    function calculateScore(issues) {
        var score = 100;
        (issues || []).forEach(function(issue) {
            if (issue.severity === 'high') score -= 15;
            else if (issue.severity === 'medium') score -= 5;
            else score -= 2;
        });
        return Math.max(0, score);
    }

    data.success = true;
})();
