sp_widget.script:
(function() {
    // Get analysis ID from URL path parameter
    // URL format: /fha_analysis_results/{analysis_id}
    var analysisId = $sp.getParameter('sys_id') || '';

    // Also check URL path for ID
    if (!analysisId) {
        var path = $sp.getParameter('id');
        // Try to extract from page route
    }

    data.analysisId = analysisId;
    data.result = null;
    data.error = null;

    if (analysisId) {
        try {
            var analyzer = new x_1310794_founda_0.FHAnalyzer();
            var result = analyzer.getAnalysisResult(analysisId);

            if (result && result.success) {
                // Get the analysis record details
                var gr = new GlideRecord('x_1310794_founda_0_fha_results');
                if (gr.get(analysisId)) {
                    // Get table name from reference
                    var tableName = '';
                    var tableLabel = '';
                    if (gr.table_name && !gr.table_name.nil()) {
                        var tableRef = gr.table_name.getRefRecord();
                        if (tableRef) {
                            tableName = tableRef.getValue('name') || '';
                            tableLabel = tableRef.getValue('label') || tableName;
                        }
                    }

                    // Parse detail_json for full metrics
                    var detailJson = {};
                    try {
                        var detailStr = gr.getValue('detail_json');
                        if (detailStr) {
                            detailJson = JSON.parse(detailStr);
                        }
                    } catch(e) {
                        // Ignore parse errors
                    }

                    data.result = {
                        sys_id: gr.getUniqueValue(),
                        number: gr.getValue('number'),
                        table_name: tableName,
                        table_label: tableLabel,
                        health_score: parseInt(gr.getValue('health_score'), 10) || 0,
                        issue_count: parseInt(gr.getValue('issue_found'), 10) || 0,
                        status: gr.getValue('status'),
                        created_on: gr.getValue('sys_created_on'),
                        created_by: gr.sys_created_by ? gr.sys_created_by.getDisplayValue() : '',
                        issues: detailJson.issues || [],
                        metrics: detailJson.metrics || {},
                        duration_ms: detailJson.metrics ? detailJson.metrics.duration_ms : 0
                    };
                } else {
                    data.error = 'Analysis record not found';
                }
        } else {
                data.error = result ? result.error : 'Failed to load analysis';
            }
        } catch(e) {
            data.error = 'Error loading analysis: ' + e.message;
        }
    } else {
        data.error = 'No analysis ID provided. Please access this page from the dashboard.';
    }
})();
