sp_widget.script:
(function() {
    // Get portal suffix
    data.portalSuffix = $sp.getPortalRecord() ? $sp.getPortalRecord().getValue('url_suffix') : 'fha';

    // Load analyses
    data.analyses = [];
    data.totalCount = 0;

    var page = (input && input.page) || 1;
    var pageSize = (input && input.pageSize) || 20;
    var search = (input && input.search) || '';
    var status = (input && input.status) || 'all';
    var scoreRange = (input && input.scoreRange) || 'all';
    var sortField = (input && input.sortField) || 'sys_created_on';
    var sortDirection = (input && input.sortDirection) || 'desc';

    // Handle actions
    if (input && input.action === 'delete_analysis') {
        var delGr = new GlideRecord('x_1310794_founda_0_fha_results');
        if (delGr.get(input.sys_id)) {
            delGr.deleteRecord();
            data.success = true;
        }
        return;
    }

    // Build query
    var gr = new GlideRecord('x_1310794_founda_0_fha_results');

    // Apply filters
    if (status !== 'all') {
        gr.addQuery('status', status);
    }

    if (search) {
        var qc = gr.addQuery('number', 'CONTAINS', search);
        qc.addOrCondition('table_name', 'CONTAINS', search);
    }

    if (scoreRange !== 'all') {
        if (scoreRange === 'good') {
            gr.addQuery('health_score', '>=', 70);
        } else if (scoreRange === 'medium') {
            gr.addQuery('health_score', '>=', 40);
            gr.addQuery('health_score', '<', 70);
        } else if (scoreRange === 'poor') {
            gr.addQuery('health_score', '<', 40);
        }
    }

    // Get total count
    var countGr = new GlideAggregate('x_1310794_founda_0_fha_results');
    if (status !== 'all') {
        countGr.addQuery('status', status);
    }
    if (search) {
        var qc2 = countGr.addQuery('number', 'CONTAINS', search);
        qc2.addOrCondition('table_name', 'CONTAINS', search);
    }
    if (scoreRange !== 'all') {
        if (scoreRange === 'good') {
            countGr.addQuery('health_score', '>=', 70);
        } else if (scoreRange === 'medium') {
            countGr.addQuery('health_score', '>=', 40);
            countGr.addQuery('health_score', '<', 70);
        } else if (scoreRange === 'poor') {
            countGr.addQuery('health_score', '<', 40);
        }
    }
    countGr.addAggregate('COUNT');
    countGr.query();
    if (countGr.next()) {
        data.totalCount = parseInt(countGr.getAggregate('COUNT'), 10);
    }

    // Apply sorting
    if (sortDirection === 'desc') {
        gr.orderByDesc(sortField);
    } else {
        gr.orderBy(sortField);
    }

    // Apply pagination
    gr.chooseWindow((page - 1) * pageSize, page * pageSize);
    gr.query();

    while (gr.next()) {
        // Get table display name
        var tableDisplayName = '';
        var tableName = '';
        if (gr.table_name && !gr.table_name.nil()) {
            var tableRef = gr.table_name.getRefRecord();
            if (tableRef) {
							gs.info("WWA table ref : "+tableRef.getValue('label'))
                tableDisplayName = tableRef.getValue('label') || tableRef.getValue('name') || '';
                tableName = tableRef.getValue('name') || '';
            }
        }


        // Parse detail_json for issue counts
        var issues = [];
        var highCount = 0;
        var mediumCount = 0;
        var lowCount = 0;

        try {
            var detailJson = gr.getValue('detail_json');
            if (detailJson) {
                var detail = JSON.parse(detailJson);
                issues = detail.issues || [];
                issues.forEach(function(issue) {
                    if (issue.severity === 'high') highCount++;
                    else if (issue.severity === 'medium') mediumCount++;
                    else if (issue.severity === 'low') lowCount++;
                });
            }
        } catch(e) {
            // Ignore parse errors
        }

        data.analyses.push({
            sys_id: gr.getUniqueValue(),
            number: gr.getValue('number') || '',
            table_name: tableName,
            table_label: tableDisplayName || 'N/A',
            health_score: parseInt(gr.getValue('health_score'), 10) || 0,
            issue_count: parseInt(gr.getValue('issue_found'), 10) || 0,
            high_count: highCount,
            medium_count: mediumCount,
            low_count: lowCount,
            status: gr.getValue('status') || '',
            created_on: gr.getValue('sys_created_on') || '',
            created_by: gr.getDisplayValue('sys_created_by') || ''
        });
    }

    // Calculate stats for header
    data.stats = {
        total: data.totalCount,
        avgScore: 0,
        goodCount: 0,
        criticalCount: 0
    };

    if (data.analyses.length > 0) {
        var totalScore = 0;
        data.analyses.forEach(function(a) {
            totalScore += a.health_score;
            if (a.health_score >= 70) data.stats.goodCount++;
            if (a.health_score < 40) data.stats.criticalCount++;
        });
        data.stats.avgScore = Math.round(totalScore / data.analyses.length);
    }

    data.success = true;
})();
