sp_widget.script:
(function() {
    // Get current user info
    data.userName = gs.getUserDisplayName();
    data.userInitials = '';

    if (data.userName) {
        var nameParts = data.userName.split(' ');
        if (nameParts.length >= 2) {
            data.userInitials = nameParts[0].charAt(0) + nameParts[nameParts.length - 1].charAt(0);
        } else if (nameParts.length === 1) {
            data.userInitials = nameParts[0].charAt(0);
        }
        data.userInitials = data.userInitials.toUpperCase();
    }

    // Get portal info
    data.portalTitle = 'Foundation Health Analyzer';
    data.portalSubtitle = 'ServiceNow Analysis Tool';
})();
