    /**
     * Parse JSON avec repli sécurisé.
     */
    _safeParse: function(val, fallback) {
        if (fallback === undefined) fallback = {};
        try {
            if (!val) return fallback;
            return JSON.parse(val);
        } catch (e) {
            return fallback;
        }
