sys_script_include.script:
/**
 * FHCheckAutomation - Check for automation issues
 * Analyzes scheduled jobs, flows, workflows, and notifications
 * 
 * Supports configuration options:
 * - deep_scan: Analyze script content in automations
 * - include_children_tables: Include automations from child tables
 */
var FHCheckAutomation = Class.create();
FHCheckAutomation.prototype = {

    /**
     * Safe getValue - returns empty string if null
     * @private
     */
    _safeGetValue: function(gr, field) {
        var val = gr.getValue(field);
        return (val === null || val === undefined) ? '' : String(val);
    },

    /**
     * Get configuration option with default value
     * @private
     */
    _getOption: function(ctx, optionName, defaultValue) {
        if (ctx.config && ctx.config[optionName] !== undefined) {
            return ctx.config[optionName];
        }
        return defaultValue;
    },

    /**
     * Execute the check
     * @param {Object} ctx - Analysis context
     */
    execute: function(ctx) {
        if (!ctx.metrics.table_exists) return;

        // Get options
        var includeChildren = this._getOption(ctx, 'include_children_tables', false);
        var deepScan = this._getOption(ctx, 'deep_scan', false);

        // Get tables to check (main table + children if option enabled)
        var tablesToCheck = this._getTablesToCheck(ctx, includeChildren);
        ctx.metrics.automation_tables_checked = tablesToCheck;

        this._checkScheduledJobs(ctx, tablesToCheck);
        this._checkFlows(ctx, tablesToCheck);
        this._checkWorkflows(ctx, tablesToCheck);
        this._checkNotifications(ctx, tablesToCheck);
        this._checkUIActions(ctx);
        this._checkUIPolicies(ctx);
        this._checkInboundEmailActions(ctx, tablesToCheck);

        // Deep scan: analyze automation scripts
        if (deepScan) {
            this._deepScanAutomations(ctx);
        }
    },

    /**
     * Get tables to check based on configuration
     * @private
     */
    _getTablesToCheck: function(ctx, includeChildren) {
        var tableName = String(ctx.config.table_name);
        var tables = [tableName];

        if (includeChildren) {
            // Get child tables
            var parentGr = new GlideRecord('sys_db_object');
            if (parentGr.get('name', tableName)) {
                var parentSysId = parentGr.getUniqueValue();

                var gr = new GlideRecord('sys_db_object');
                gr.addQuery('super_class', parentSysId);
                gr.query();

                while (gr.next()) {
                    var childName = this._safeGetValue(gr, 'name');
                    if (childName && tables.indexOf(childName) === -1) {
                        tables.push(childName);
                    }
                }
            }
        }

        return tables;
    },

    /**
     * Check if script contains exact table reference (not partial match)
     * @private
     * @param {String} script - Script content
     * @param {String} tableName - Table name to search
     * @returns {Boolean} True if exact match found
     */
    _scriptContainsTable: function(script, tableName) {
        if (!script || !tableName) return false;

        // Patterns that indicate exact table usage:
        // 1. GlideRecord('table_name') or GlideRecord("table_name")
        // 2. new GlideAggregate('table_name')
        // 3. table: 'table_name' or table: "table_name"
        // 4. addQuery('table', 'table_name')

        // Build regex patterns for exact table name match
        var patterns = [
            // GlideRecord/GlideAggregate with quotes
            new RegExp("GlideRecord\\s*\\(\\s*['\"]" + tableName + "['\"]\\s*\\)", "i"),
            new RegExp("GlideAggregate\\s*\\(\\s*['\"]" + tableName + "['\"]\\s*\\)", "i"),
            // Table property assignment
            new RegExp("table\\s*[=:]\\s*['\"]" + tableName + "['\"]", "i"),
            // setTableName
            new RegExp("setTableName\\s*\\(\\s*['\"]" + tableName + "['\"]\\s*\\)", "i")
        ];

        for (var i = 0; i < patterns.length; i++) {
            if (patterns[i].test(script)) {
                return true;
            }
        }

        return false;
    },

    /**
     * Check scheduled jobs that reference this table
     * @private
     * @param {Object} ctx - Analysis context
     * @param {Array} tablesToCheck - Tables to check
     */
    _checkScheduledJobs: function(ctx, tablesToCheck) {
        var tableName = String(ctx.config.table_name);
        var scheduledJobs = [];
        var self = this;

        // Query all scheduled jobs with scripts that might contain our tables
        // We'll do a broader search first, then filter with exact matching
        var gr = new GlideRecord('sysauto_script');
        gr.addNotNullQuery('script');
        gr.query();

        while (gr.next()) {
            var script = self._safeGetValue(gr, 'script');

            // Check if script contains exact reference to any of our tables
            var matchedTable = null;
            for (var i = 0; i < tablesToCheck.length; i++) {
                if (self._scriptContainsTable(script, tablesToCheck[i])) {
                    matchedTable = tablesToCheck[i];
                    break;
                }
            }

            if (!matchedTable) continue; // Skip if no exact match

            var jobName = self._safeGetValue(gr, 'name') || 'Unnamed';
            // Check active field - can be 'true', '1', or boolean
            var activeVal = gr.getValue('active');
            var isActive = (activeVal === 'true' || activeVal === '1' || activeVal === true);

            var jobInfo = {
                sys_id: gr.getUniqueValue(),
                name: jobName,
                active: isActive,
                matched_table: matchedTable,
                run_type: self._safeGetValue(gr, 'run_type'),
                next_action: self._safeGetValue(gr, 'next_action')
            };
            scheduledJobs.push(jobInfo);

            if (!isActive) {
                ctx.addIssue('INACTIVE_SCHEDULED_JOB', 'Inactive scheduled job: "' + jobName + '"', 'low', {
                    record_table: 'sysauto_script',
                    record_sys_id: gr.getUniqueValue(),
                    category: 'automation'
                });
            }
        }

        ctx.metrics.scheduled_jobs = scheduledJobs;
        ctx.metrics.scheduled_job_count = scheduledJobs.length;
    },

    /**
     * Check Flow Designer flows for this table (including inherited from parent tables)
     * @private
     * @param {Object} ctx - Analysis context
     * @param {Array} configTables - Tables from configuration (with children if enabled)
     */
    _checkFlows: function(ctx, configTables) {
        var tableName = String(ctx.config.table_name);
        var flows = [];
        var self = this;

        // Get all tables in hierarchy (parent tables)
        var parentTables = self._getTableHierarchy(tableName);

        // Merge with config tables (children) and remove duplicates
        var tablesToCheck = parentTables.slice();
        configTables.forEach(function(t) {
            if (tablesToCheck.indexOf(t) === -1) {
                tablesToCheck.push(t);
            }
        });

        // Check for flows triggered by this table or parent tables
        try {
            var gr = new GlideRecord('sys_hub_flow');
            if (gr.isValid()) {
                gr.addQuery('trigger_table', 'IN', tablesToCheck.join(','));
                gr.query();

                while (gr.next()) {
                    var flowName = self._safeGetValue(gr, 'name') || 'Unnamed';
                    var flowTable = self._safeGetValue(gr, 'trigger_table');
                    // Check active field - can be 'true', '1', or boolean
                    var activeVal = gr.getValue('active');
                    var isActive = (activeVal === 'true' || activeVal === '1' || activeVal === true);

                    var flowInfo = {
                        sys_id: gr.getUniqueValue(),
                        name: flowName,
                        table: flowTable,
                        inherited: (flowTable !== tableName),
                        active: isActive,
                        trigger_type: self._safeGetValue(gr, 'trigger_type'),
                        description: self._safeGetValue(gr, 'description')
                    };
                    flows.push(flowInfo);

                    if (!isActive) {
                        ctx.addIssue('INACTIVE_FLOW', 'Inactive flow: "' + flowName + '"', 'low', {
                            record_table: 'sys_hub_flow',
                            record_sys_id: gr.getUniqueValue(),
                            category: 'automation'
                        });
                    }
                }
            }
        } catch (e) {
            // Flow Designer table might not exist in all instances
        }

        ctx.metrics.flows = flows;
        ctx.metrics.flow_count = flows.length;
        ctx.metrics.flow_direct_count = flows.filter(function(f) { return !f.inherited; }).length;
        ctx.metrics.flow_inherited_count = flows.filter(function(f) { return f.inherited; }).length;

        if (flows.length > 10) {
            ctx.addIssue('MANY_FLOWS', 'Table has ' + flows.length + ' flows (' + ctx.metrics.flow_direct_count + ' direct, ' + ctx.metrics.flow_inherited_count + ' inherited). Check for redundancy.', 'medium', {
                record_table: 'sys_hub_flow',
                record_filter: 'trigger_tableIN' + tablesToCheck.join(','),
                category: 'automation'
            });
        }
    },

    /**
     * Check legacy workflows for this table (including inherited from parent tables)
     * @private
     * @param {Object} ctx - Analysis context
     * @param {Array} configTables - Tables from configuration (with children if enabled)
     */
    _checkWorkflows: function(ctx, configTables) {
        var tableName = String(ctx.config.table_name);
        var workflows = [];
        var self = this;

        // Get all tables in hierarchy (parent tables)
        var parentTables = self._getTableHierarchy(tableName);

        // Merge with config tables (children) and remove duplicates
        var tablesToCheck = parentTables.slice();
        configTables.forEach(function(t) {
            if (tablesToCheck.indexOf(t) === -1) {
                tablesToCheck.push(t);
            }
        });

        var gr = new GlideRecord('wf_workflow');
        gr.addQuery('table', 'IN', tablesToCheck.join(','));
        gr.query();

        while (gr.next()) {
            var wfName = self._safeGetValue(gr, 'name') || 'Unnamed';
            var wfTable = self._safeGetValue(gr, 'table');
            // Check active and published fields
            var activeVal = gr.getValue('active');
            var publishedVal = gr.getValue('published');
            var isActive = (activeVal === 'true' || activeVal === '1' || activeVal === true);
            var isPublished = (publishedVal === 'true' || publishedVal === '1' || publishedVal === true);

            var wfInfo = {
                sys_id: gr.getUniqueValue(),
                name: wfName,
                table: wfTable,
                inherited: (wfTable !== tableName),
                active: isActive,
                published: isPublished
            };
            workflows.push(wfInfo);

            // Check for unpublished active workflows
            if (isActive && !isPublished) {
                ctx.addIssue('UNPUBLISHED_WORKFLOW', 'Active but unpublished workflow: "' + wfName + '"', 'medium', {
                    record_table: 'wf_workflow',
                    record_sys_id: gr.getUniqueValue(),
                    category: 'automation'
                });
            }
        }

        ctx.metrics.workflows = workflows;
        ctx.metrics.workflow_count = workflows.length;
        ctx.metrics.workflow_direct_count = workflows.filter(function(w) { return !w.inherited; }).length;
        ctx.metrics.workflow_inherited_count = workflows.filter(function(w) { return w.inherited; }).length;

        // Recommend migrating to Flow Designer if using legacy workflows
        if (workflows.length > 0) {
            ctx.addIssue('LEGACY_WORKFLOWS', 'Table uses ' + workflows.length + ' legacy workflow(s) (' + ctx.metrics.workflow_direct_count + ' direct, ' + ctx.metrics.workflow_inherited_count + ' inherited). Consider migrating to Flow Designer.', 'low', {
                record_table: 'wf_workflow',
                record_filter: 'tableIN' + tablesToCheck.join(','),
                category: 'automation'
            });
        }
    },

    /**
     * Check notifications for this table (including inherited from parent tables)
     * @private
     * @param {Object} ctx - Analysis context
     * @param {Array} configTables - Tables from configuration (with children if enabled)
     */
    _checkNotifications: function(ctx, configTables) {
        var tableName = String(ctx.config.table_name);
        var notifications = [];
        var self = this;

        // Get all tables in hierarchy (parent tables)
        var parentTables = self._getTableHierarchy(tableName);

        // Merge with config tables (children) and remove duplicates
        var tablesToCheck = parentTables.slice();
        configTables.forEach(function(t) {
            if (tablesToCheck.indexOf(t) === -1) {
                tablesToCheck.push(t);
            }
        });

        var gr = new GlideRecord('sysevent_email_action');
        gr.addQuery('collection', 'IN', tablesToCheck.join(','));
        gr.query();

        while (gr.next()) {
            var notifTable = self._safeGetValue(gr, 'collection');
            var activeVal = gr.getValue('active');
            var isActive = (activeVal === 'true' || activeVal === '1' || activeVal === true);

            var notifInfo = {
                sys_id: gr.getUniqueValue(),
                name: self._safeGetValue(gr, 'name') || 'Unnamed',
                table: notifTable,
                inherited: (notifTable !== tableName),
                active: isActive,
                event_name: self._safeGetValue(gr, 'event_name'),
                send_self: self._safeGetValue(gr, 'send_self') === 'true'
            };
            notifications.push(notifInfo);
        }

        ctx.metrics.notifications = notifications;
        ctx.metrics.notification_count = notifications.length;
        ctx.metrics.notification_direct_count = notifications.filter(function(n) { return !n.inherited; }).length;
        ctx.metrics.notification_inherited_count = notifications.filter(function(n) { return n.inherited; }).length;

        if (notifications.length > 20) {
            ctx.addIssue('MANY_NOTIFICATIONS', 'Table has ' + notifications.length + ' notifications (' + ctx.metrics.notification_direct_count + ' direct, ' + ctx.metrics.notification_inherited_count + ' inherited). Check for potential spam.', 'medium', {
                record_table: 'sysevent_email_action',
                record_filter: 'collectionIN' + tablesToCheck.join(','),
                category: 'notification'
            });
        }
    },

    /**
     * Check UI Actions for this table (including inherited from parent tables)
     * @private
     * @param {Object} ctx - Analysis context
     */
    _checkUIActions: function(ctx) {
        var tableName = String(ctx.config.table_name);
        var uiActions = [];
        var self = this;

        // Get all tables in hierarchy (current table + parent tables)
        var tablesToCheck = self._getTableHierarchy(tableName);

        // Query all UI Actions (active and inactive) for complete overview
        // Use GlideRecordSecure to ensure we can read across scopes
        var gr = new GlideRecord('sys_ui_action');

        // Build query - check each table individually to avoid IN clause issues
        if (tablesToCheck.length === 1) {
            gr.addQuery('table', tablesToCheck[0]);
        } else {
            var qc = gr.addQuery('table', tablesToCheck[0]);
            for (var i = 1; i < tablesToCheck.length; i++) {
                qc.addOrCondition('table', tablesToCheck[i]);
            }
        }
        gr.query();

        while (gr.next()) {
            var actionTable = self._safeGetValue(gr, 'table');
            var activeVal = gr.getValue('active');
            var isActive = (activeVal === 'true' || activeVal === '1' || activeVal === true);

            uiActions.push({
                sys_id: gr.getUniqueValue(),
                name: self._safeGetValue(gr, 'name') || 'Unnamed',
                action_name: self._safeGetValue(gr, 'action_name'),
                table: actionTable,
                inherited: (actionTable !== tableName),
                active: isActive,
                client: self._safeGetValue(gr, 'client') === 'true',
                form_button: self._safeGetValue(gr, 'form_button') === 'true',
                list_button: self._safeGetValue(gr, 'list_button') === 'true'
            });
        }

        ctx.metrics.ui_actions = uiActions;
        ctx.metrics.ui_action_count = uiActions.length;
        ctx.metrics.ui_action_active_count = uiActions.filter(function(a) { return a.active; }).length;
        ctx.metrics.ui_action_inactive_count = uiActions.filter(function(a) { return !a.active; }).length;
        ctx.metrics.ui_action_direct_count = uiActions.filter(function(a) { return !a.inherited; }).length;
        ctx.metrics.ui_action_inherited_count = uiActions.filter(function(a) { return a.inherited; }).length;

        // Store tables checked for debugging
        ctx.metrics.ui_action_tables_checked = tablesToCheck;

        if (uiActions.length > 25) {
            ctx.addIssue('MANY_UI_ACTIONS', 'Table has ' + uiActions.length + ' UI Actions (' + ctx.metrics.ui_action_active_count + ' active, ' + ctx.metrics.ui_action_direct_count + ' direct). Interface may be overloaded.', 'medium', {
                record_table: 'sys_ui_action',
                record_filter: 'tableIN' + tablesToCheck.join(','),
                category: 'ui'
            });
        }
    },

    /**
     * Get table hierarchy (current table + all parent tables)
     * @private
     * @param {String} tableName - Table name
     * @returns {Array} Array of table names
     */
    _getTableHierarchy: function(tableName) {
        var tables = [tableName];

        try {
            var tableGr = new GlideRecord('sys_db_object');
            tableGr.addQuery('name', tableName);
            tableGr.query();

            if (tableGr.next()) {
                var superClass = tableGr.getValue('super_class');
                while (superClass) {
                    var parentGr = new GlideRecord('sys_db_object');
                    if (parentGr.get(superClass)) {
                        var parentName = parentGr.getValue('name');
                        if (parentName && tables.indexOf(parentName) === -1) {
                            tables.push(parentName);
                        }
                        superClass = parentGr.getValue('super_class');
                    } else {
                        break;
                    }
                }
            }
        } catch(e) {
            // If we can't get hierarchy, just use the table itself
        }

        return tables;
    },

    /**
     * Check UI Policies for this table (including inherited from parent tables)
     * @private
     * @param {Object} ctx - Analysis context
     */
    _checkUIPolicies: function(ctx) {
        var tableName = String(ctx.config.table_name);
        var uiPolicies = [];
        var self = this;

        // Get all tables in hierarchy (current table + parent tables)
        var tablesToCheck = self._getTableHierarchy(tableName);

        var gr = new GlideRecord('sys_ui_policy');
        gr.addQuery('table', 'IN', tablesToCheck.join(','));
        gr.addQuery('active', true);
        gr.query();

        while (gr.next()) {
            var policyTable = self._safeGetValue(gr, 'table');
            uiPolicies.push({
                sys_id: gr.getUniqueValue(),
                name: self._safeGetValue(gr, 'short_description') || 'Unnamed',
                table: policyTable,
                inherited: (policyTable !== tableName),
                on_load: self._safeGetValue(gr, 'on_load') === 'true',
                reverse_if_false: self._safeGetValue(gr, 'reverse_if_false') === 'true',
                global: self._safeGetValue(gr, 'global') === 'true'
            });
        }

        ctx.metrics.ui_policies = uiPolicies;
        ctx.metrics.ui_policy_count = uiPolicies.length;
        ctx.metrics.ui_policy_direct_count = uiPolicies.filter(function(p) { return !p.inherited; }).length;
        ctx.metrics.ui_policy_inherited_count = uiPolicies.filter(function(p) { return p.inherited; }).length;

        if (uiPolicies.length > 15) {
            ctx.addIssue('MANY_UI_POLICIES', 'Table has ' + uiPolicies.length + ' active UI Policies (' + ctx.metrics.ui_policy_direct_count + ' direct, ' + ctx.metrics.ui_policy_inherited_count + ' inherited). This may slow down form loading.', 'medium', {
                record_table: 'sys_ui_policy',
                record_filter: 'tableIN' + tablesToCheck.join(',') + '^active=true',
                category: 'ui'
            });
        }
    },

    /**
     * Check inbound email actions for this table
     * @private
     * @param {Object} ctx - Analysis context
     * @param {Array} tablesToCheck - Tables to check
     */
    _checkInboundEmailActions: function(ctx, tablesToCheck) {
        var tableName = String(ctx.config.table_name);
        var inboundEmails = [];
        var self = this;

        // Get all tables in hierarchy (current table + parent tables)
        var parentTables = self._getTableHierarchy(tableName);

        // Merge with config tables (children) and remove duplicates
        var allTables = parentTables.slice();
        tablesToCheck.forEach(function(t) {
            if (allTables.indexOf(t) === -1) {
                allTables.push(t);
            }
        });

        // Check sysevent_in_email_action (Inbound Email Actions)
        try {
            var gr = new GlideRecord('sysevent_in_email_action');
            if (gr.isValid()) {
                gr.addQuery('target_table', 'IN', allTables.join(','));
                gr.query();

                while (gr.next()) {
                    var emailTable = self._safeGetValue(gr, 'target_table');
                    var activeVal = gr.getValue('active');
                    var isActive = (activeVal === 'true' || activeVal === '1' || activeVal === true);

                    var emailInfo = {
                        sys_id: gr.getUniqueValue(),
                        name: self._safeGetValue(gr, 'name') || 'Unnamed',
                        table: emailTable,
                        inherited: (emailTable !== tableName),
                        active: isActive,
                        type: self._safeGetValue(gr, 'type'),
                        action_type: self._safeGetValue(gr, 'action_type'),
                        stop_processing: self._safeGetValue(gr, 'stop_processing') === 'true'
                    };
                    inboundEmails.push(emailInfo);

                    if (!isActive) {
                        ctx.addIssue('INACTIVE_INBOUND_EMAIL', 'Inactive inbound email action: "' + emailInfo.name + '"', 'low', {
                            record_table: 'sysevent_in_email_action',
                            record_sys_id: gr.getUniqueValue(),
                            category: 'automation'
                        });
                    }
                }
            }
        } catch (e) {
            // Table might not be accessible
        }

        // Check sys_hub_trigger_instance (Flow Triggers including email)
        try {
            var triggerGr = new GlideRecord('sys_hub_trigger_instance');
            if (triggerGr.isValid()) {
                // Email triggers
                triggerGr.addQuery('trigger_type', 'CONTAINS', 'email');
                triggerGr.query();

                while (triggerGr.next()) {
                    // Check if trigger is related to our table
                    var triggerConfig = self._safeGetValue(triggerGr, 'trigger_config');
                    var isRelated = false;

                    allTables.forEach(function(t) {
                        if (triggerConfig && triggerConfig.indexOf(t) > -1) {
                            isRelated = true;
                        }
                    });

                    if (isRelated) {
                        var flowRef = triggerGr.flow;
                        var flowName = '';
                        if (flowRef && !flowRef.nil()) {
                            var flowGr = flowRef.getRefRecord();
                            if (flowGr) {
                                flowName = self._safeGetValue(flowGr, 'name');
                            }
                        }

                        inboundEmails.push({
                            sys_id: triggerGr.getUniqueValue(),
                            name: flowName || 'Flow Trigger',
                            table: tableName,
                            inherited: false,
                            active: true,
                            type: 'flow_trigger',
                            action_type: 'email',
                            stop_processing: false
                        });
                    }
                }
            }
        } catch (e) {
            // Table might not exist
        }

        ctx.metrics.inbound_email_actions = inboundEmails;
        ctx.metrics.inbound_email_count = inboundEmails.length;
        ctx.metrics.inbound_email_active_count = inboundEmails.filter(function(e) { return e.active; }).length;
        ctx.metrics.inbound_email_direct_count = inboundEmails.filter(function(e) { return !e.inherited; }).length;

        if (inboundEmails.length > 10) {
            ctx.addIssue('MANY_INBOUND_EMAILS', 'Table has ' + inboundEmails.length + ' inbound email actions. Review for efficiency.', 'low', {
                record_table: 'sysevent_in_email_action',
                record_filter: 'target_tableIN' + allTables.join(','),
                category: 'automation'
            });
        }
    },

    /**
     * Deep scan automations for script quality (if deep_scan option is enabled)
     * @private
     * @param {Object} ctx - Analysis context
     */
    _deepScanAutomations: function(ctx) {
        var tableName = String(ctx.config.table_name);
        var automationIssues = [];
        var self = this;

        // Scan Flows for complexity
        if (ctx.metrics.flows && ctx.metrics.flows.length > 0) {
            ctx.metrics.flows.forEach(function(flow) {
                if (flow.active) {
                    // Check for flows without description
                    if (!flow.description) {
                        automationIssues.push({
                            type: 'Flow',
                            name: flow.name,
                            code: 'NO_DESCRIPTION',
                            message: 'Flow "' + flow.name + '" has no description',
                            severity: 'low'
                        });
                    }
                }
            });
        }

        // Scan Scheduled Jobs for script issues
        if (ctx.metrics.scheduled_jobs && ctx.metrics.scheduled_jobs.length > 0) {
            var jobGr = new GlideRecord('sysauto_script');
            ctx.metrics.scheduled_jobs.forEach(function(job) {
                if (jobGr.get(job.sys_id)) {
                    var script = self._safeGetValue(jobGr, 'script');
                    var issues = self._analyzeAutomationScript(script, job.name, 'Scheduled Job');
                    automationIssues = automationIssues.concat(issues);
                }
            });
        }

        ctx.metrics.automation_script_issues = automationIssues;
        ctx.metrics.automation_script_issue_count = automationIssues.length;

        // Add issues
        automationIssues.forEach(function(issue) {
            ctx.addIssue(issue.code, issue.message, issue.severity, {
                category: 'automation'
            });
        });
    },

    /**
     * Analyze automation script for common issues
     * @private
     */
    _analyzeAutomationScript: function(script, scriptName, scriptType) {
        var issues = [];
        if (!script) return issues;

        // Check for GlideRecord in loop without limit
        if (script.indexOf('GlideRecord') > -1 && script.indexOf('.query()') > -1) {
            if (script.indexOf('setLimit') === -1 && script.indexOf('get(') === -1) {
                if (script.indexOf('while') > -1 && script.indexOf('.next()') > -1) {
                    issues.push({
                        type: scriptType,
                        name: scriptName,
                        code: 'QUERY_NO_LIMIT',
                        message: scriptType + ' "' + scriptName + '" has GlideRecord query without setLimit()',
                        severity: 'medium'
                    });
                }
            }
        }

        // Check for hardcoded sys_ids (32 hex characters)
        var sysIdPattern = /['"]\s*[a-fA-F0-9]{32}\s*['"]/g;
        var sysIdMatches = script.match(sysIdPattern);
        if (sysIdMatches && sysIdMatches.length > 0) {
            issues.push({
                type: scriptType,
                name: scriptName,
                code: 'HARDCODED_SYSID',
                message: scriptType + ' "' + scriptName + '" contains ' + sysIdMatches.length + ' hardcoded sys_id value(s)',
                severity: 'medium'
            });
        }

        // Check for gs.sleep (can cause performance issues)
        if (script.indexOf('gs.sleep') > -1) {
            issues.push({
                type: scriptType,
                name: scriptName,
                code: 'GS_SLEEP',
                message: scriptType + ' "' + scriptName + '" uses gs.sleep() which can impact performance',
                severity: 'medium'
            });
        }

        // Check for eval usage
        if (script.indexOf('eval(') > -1) {
            issues.push({
                type: scriptType,
                name: scriptName,
                code: 'EVAL_USAGE',
                message: scriptType + ' "' + scriptName + '" uses eval() which is a security risk',
                severity: 'high'
            });
        }

        return issues;
    },

    type: 'FHCheckAutomation'
};
