    // Specific scheduled jobs checker â€” uses the generic above.
    // Comments in English.
    checkScheduledJobs: function(ctx, configTables, queryTable) {
        // buildQuery: add any additional query constraints to the GlideRecord
        var buildQuery = function(gr) {

            if (gr && typeof gr.addNotNullQuery === 'function') {
                gr.addNotNullQuery('script');
				gr.addQuery("scriptLIKEchange_request");
            }
        };
gs.info('WWA build query' + buildQuery);
        var fields = ['name', 'script', 'active', 'sys_created_by', 'sys_updated_by', 'run_type', 'sys_id'];

        // matcher: detect if the script contains any of the configured table names
        var matcher = function(rec, cfgTables) {
            var script = '';
            // prefer GlideRecord getValue for safe access
            if (rec) {
                if (typeof rec.getValue === 'function') script = (rec.getValue('script') || '');
                else script = (rec.script || '') + '';
            }
            for (var i = 0; i < cfgTables.length; i++) {
                if (script.indexOf(cfgTables[i]) > -1) return cfgTables[i];
            }
            return false;
        };

        // transform: normalize the record into a simple object used by reporting
        var transform = function(rec, matched) {
            // safe getter that works with GlideRecord or plain object
            var get = function(field) {
                if (!rec) return undefined;
                if (typeof rec.getValue === 'function') return rec.getValue(field);
                return rec[field];
            };

            // prefer getUniqueValue if available for sys_id
            var id = '';
            try {
                if (rec && typeof rec.getUniqueValue === 'function') id = rec.getUniqueValue();
                else id = get('sys_id') || '';
            } catch (e) {
                id = get('sys_id') || '';
            }

            return {
                sys_id: id,
                name: get('name') || 'Unnamed',
                active: get('active'),
                inherited: matched,
                matched_table: matched,
                run_type: get('run_type'),
                sys_created_by: get('sys_created_by'),
                sys_updated_by: get('sys_updated_by')
            };
        };

        // call generic checker (note: we pass meta inline)
        return this.utils.checkRecordsGeneric(ctx, configTables, queryTable || 'sysauto_script', buildQuery, fields, {
            record_table: 'sysauto_script',
            record_type: 'scheduled_job',
            displayName: 'Scheduled Jobs',
            icon: 'fa-clock',
            color: '#6f42c1',
            inactivityIssueCode: 'INACTIVE_SCHEDULED_JOB',
            category: 'automation',
            manyThreshold: 50,
            manyIssueCode: 'MANY_SCHEDULED_JOBS'
        }, matcher, transform);
    },

    /**
     * checkFlows(ctx, configTables)
     * Check flows (sys_hub_flow) for trigger_table matching our config tables.
     */
    checkFlows: function(ctx, configTables, queryTable) {
        var tableName = ctx.getTableName();
        var flows = [];

        try {
            var rawFlowIds = [];
            for (var x = 0; x < configTables.length; x++) {
                var ct = configTables[x];
                try {
                    var result = sn_flow && sn_flow.AssociatedFlows && typeof sn_flow.AssociatedFlows.getFlows === 'function' ?
                        sn_flow.AssociatedFlows.getFlows(ct) : [];
                    if (result == null) {
                        /* nothing */
                    } else if (typeof result === 'string') {
                        result.split(',').forEach(function(i) {
                            if (i) rawFlowIds.push(i.trim());
                        });
                    } else if (Array.isArray(result)) {
                        for (var r = 0; r < result.length; r++) {
                            var item = result[r];
                            if (!item) continue;
                            if (typeof item === 'string') rawFlowIds.push(item);
                            else if (item.sys_id) rawFlowIds.push(String(item.sys_id));
                            else if (item.getUniqueValue && typeof item.getUniqueValue === 'function') rawFlowIds.push(String(item.getUniqueValue()));
                        }
                    } else if (result.sys_id) {
                        rawFlowIds.push(String(result.sys_id));
                    } else {
                        gs.info('checkFlows: unexpected shape from getFlows for table ' + ct + ' => ' + JSON.stringify(result));
                    }
                } catch (e) {
                    gs.error('checkFlows: error calling sn_flow.AssociatedFlows.getFlows for ' + ct + ' : ' + (e && e.message));
                }
            }

            var seen = {};
            var flowIds = [];
            for (var iId = 0; iId < rawFlowIds.length; iId++) {
                var fid = rawFlowIds[iId];
                if (fid && !seen[fid]) {
                    seen[fid] = true;
                    flowIds.push(fid);
                }
            }

            if (flowIds.length === 0) {
                gs.info('checkFlows: no flow sys_ids found for configTables: ' + configTables.join(','));
            } else {
                var inList = flowIds.join(',');
                var flowGR = this.utils.getRecords(queryTable, function(gr) {
                    gr.addQuery('sys_id', 'IN', inList);
                }, ['name', 'active', 'sys_created_by', 'sys_updated_by', 'description', 'sys_id'], 0);

                for (var j = 0; j < flowGR.length; j++) {
                    var flow = flowGR[j];
                    if (!flow) continue;

                    // script field may be undefined; default to empty string
                    var scriptText = (flow.script || '') + '';
                    var matchedTable = null;
                    for (var t = 0; t < configTables.length; t++) {
                        if (scriptText.indexOf(configTables[t]) > -1) {
                            matchedTable = configTables[t];
                            break;
                        }
                    }

                    var isActive = this.utils.toBool(flow.active);
                    var flowItem = {
                        sys_id: flow.sys_id,
                        name: flow.name || 'Unnamed',
                        active: isActive,
                        inherited: matchedTable,
                        table: queryTable,
                        matched_table: matchedTable,
                        sys_created_by: flow.sys_created_by,
                        sys_updated_by: flow.sys_updated_by,
                        description: flow.description
                    };

                    if (ctx.isIgnoreSncRecordsEnabled && ctx.isIgnoreSncRecordsEnabled() &&
                        this.utils.isSncRecord && this.utils.isSncRecord(flowItem)) {
                        continue;
                    }

                    flows.push(flowItem);
                }

                for (var k = 0; k < flows.length; k++) {
                    var fItem = flows[k];
                    if (!fItem.active) {
                        this.utils.addIssueUnlessSnc(ctx, 'INACTIVE_FLOW',
                            'Inactive flow: "' + (fItem.name || 'Unnamed') + '"', 'low', {
                                record_table: 'sys_hub_flow',
                                record_sys_id: fItem.sys_id,
                                category: 'automation'
                            }, fItem);
                    }
                }
            }
        } catch (e) {
            gs.error('FHCheckAutomation.checkFlows error: ' + (e && e.message) + ' / ' + (e && e.stack));
        }

        this.utils.addCategoryAndMetrics(ctx, 'automation', 'flow', 'Flows', flows, {
            table: 'sys_hub_flow',
            icon: 'fa-project-diagram',
            color: '#17a2b8'
        });

        if (flows.length > 10) {
            ctx.addIssue('MANY_FLOWS', 'Table has ' + flows.length + ' flows. Check for redundancy.', 'medium', {
                record_table: 'sys_hub_flow',
                record_filter: 'trigger_tableIN' + configTables.join(','),
                category: 'automation'
            });
        }
    },



    /**
     * checkWorkflows(ctx, configTables)
     * Legacy workflows (wf_workflow) that target our tables.
     */
    checkWorkflows: function(ctx, configTables) {
        var tableName = ctx.getTableName();
        var workflows = [];

        try {
            var gr = new GlideRecord('wf_workflow');
            this.utils.addInQuery(gr, 'table', configTables);
            gr.query();
            while (gr.next()) {
                var wfName = ctx.safeGetValue(gr, 'name') || 'Unnamed';
                var wfTable = ctx.safeGetValue(gr, 'table');
                var isActive = this.utils.toBool(gr.getValue('active'));
                var isPublished = this.utils.toBool(gr.getValue('published'));
                var wfItem = {
                    sys_id: gr.getUniqueValue(),
                    name: wfName,
                    table: 'wf_workflow',
                    trigger_table: wfTable,
                    inherited: (wfTable !== tableName),
                    active: isActive,
                    published: isPublished,
                    description: ctx.safeGetValue(gr, 'description'),
                    sys_created_by: ctx.safeGetValue(gr, 'sys_created_by'),
                    sys_updated_by: ctx.safeGetValue(gr, 'sys_updated_by')
                };

                if (ctx.isIgnoreSncRecordsEnabled && ctx.isIgnoreSncRecordsEnabled() &&
                    this.utils.isSncRecord && this.utils.isSncRecord(wfItem)) {
                    continue;
                }

                workflows.push(wfItem);

                if (!isActive || !isPublished) {
                    var code = !isPublished ? 'UNPUBLISHED_WORKFLOW' : 'INACTIVE_WORKFLOW';
                    var msg = (!isPublished ? 'Unpublished' : 'Inactive') + ' workflow: "' + wfName + '"';
                    this.utils.addIssueUnlessSnc(ctx, code, msg, 'low', {
                        record_table: 'wf_workflow',
                        record_sys_id: gr.getUniqueValue(),
                        category: 'automation'
                    }, wfItem);
                }
            }
        } catch (e) {
            gs.error('FHCheckAutomation.checkWorkflows error: ' + (e && e.message));
        }

        this.utils.addCategoryAndMetrics(ctx, 'automation', 'workflow', 'Workflows', workflows, {
            table: 'wf_workflow',
            icon: 'fa-project-diagram',
            color: '#6610f2'
        });

        if (workflows.length > 10) {
            ctx.addIssue('MANY_WORKFLOWS', 'Table has ' + workflows.length + ' workflows. Review for consolidation.', 'medium', {
                record_table: 'wf_workflow',
                record_filter: 'tableIN' + configTables.join(','),
                category: 'automation'
            });
        }
    },

    /**
     * checkNotifications(ctx, configTables)
     * Notifications that reference the table in conditions or scripts.
     */
    checkNotifications: function(ctx, configTables) {
        var notifications = [];
        try {
            var gr = new GlideRecord('sysevent_email_action'); // notifications table
            gr.addNotNullQuery('condition'); // likely to have conditions referencing fields
            gr.query();
            while (gr.next()) {
                var name = ctx.safeGetValue(gr, 'name') || 'Unnamed';
                var condition = ctx.safeGetValue(gr, 'condition') || '';
                var script = ctx.safeGetValue(gr, 'script') || '';
                var related = false;
                for (var t = 0; t < configTables.length; t++) {
                    if (condition.indexOf(configTables[t]) > -1 || script.indexOf(configTables[t]) > -1) {
                        related = true;
                        break;
                    }
                }
                if (!related) continue;

                var isActive = this.utils.toBool(gr.getValue('active'));
                var notifItem = {
                    sys_id: gr.getUniqueValue(),
                    name: name,
                    table: 'sysevent_email_action',
                    active: isActive,
                    condition: condition,
                    sys_created_by: ctx.safeGetValue(gr, 'sys_created_by'),
                    sys_updated_by: ctx.safeGetValue(gr, 'sys_updated_by')
                };

                if (ctx.isIgnoreSncRecordsEnabled && ctx.isIgnoreSncRecordsEnabled() &&
                    this.utils.isSncRecord && this.utils.isSncRecord(notifItem)) {
                    continue;
                }

                notifications.push(notifItem);

                if (!isActive) {
                    this.utils.addIssueUnlessSnc(ctx, 'INACTIVE_NOTIFICATION',
                        'Inactive notification: "' + name + '"', 'low', {
                            record_table: 'sysevent_email_action',
                            record_sys_id: gr.getUniqueValue(),
                            category: 'automation'
                        }, notifItem);
                }
            }
        } catch (e) {
            gs.error('FHCheckAutomation.checkNotifications error: ' + (e && e.message));
        }

        this.utils.addCategoryAndMetrics(ctx, 'automation', 'notification', 'Notifications', notifications, {
            table: 'sysevent_email_action',
            icon: 'fa-bell',
            color: '#ffc107'
        });
    },

    /**
     * checkUIActions(ctx, configTables)
     * UI Actions (sys_ui_action) related to our tables.
     */
    checkUIActions: function(ctx, configTables) {
        var uiActions = [];
        try {
            var gr = new GlideRecord('sys_ui_action');
            this.utils.addInQuery(gr, 'table', configTables);
            gr.query();
            while (gr.next()) {
                var name = ctx.safeGetValue(gr, 'name') || 'Unnamed';
                var active = this.utils.toBool(gr.getValue('active'));
                var table = ctx.safeGetValue(gr, 'table');
                var actionType = ctx.safeGetValue(gr, 'type');
                var script = ctx.safeGetValue(gr, 'script') || '';
                var uiItem = {
                    sys_id: gr.getUniqueValue(),
                    name: name,
                    table: 'sys_ui_action',
                    target_table: table,
                    inherited: (table !== ctx.getTableName()),
                    active: active,
                    type: actionType,
                    script: script,
                    sys_created_by: ctx.safeGetValue(gr, 'sys_created_by'),
                    sys_updated_by: ctx.safeGetValue(gr, 'sys_updated_by')
                };

                if (ctx.isIgnoreSncRecordsEnabled && ctx.isIgnoreSncRecordsEnabled() &&
                    this.utils.isSncRecord && this.utils.isSncRecord(uiItem)) {
                    continue;
                }

                uiActions.push(uiItem);

                if (!active) {
                    this.utils.addIssueUnlessSnc(ctx, 'INACTIVE_UI_ACTION',
                        'Inactive UI Action: "' + name + '"', 'low', {
                            record_table: 'sys_ui_action',
                            record_sys_id: gr.getUniqueValue(),
                            category: 'automation'
                        }, uiItem);
                }
            }
        } catch (e) {
            gs.error('FHCheckAutomation.checkUIActions error: ' + (e && e.message));
        }

        this.utils.addCategoryAndMetrics(ctx, 'automation', 'ui_action', 'UI Actions', uiActions, {
            table: 'sys_ui_action',
            icon: 'fa-mouse-pointer',
            color: '#007bff'
        });

        if (uiActions.length > 25) {
            ctx.addIssue('MANY_UI_ACTIONS', 'Table has ' + uiActions.length + ' UI Actions. Interface may be overloaded.', 'medium', {
                record_table: 'sys_ui_action',
                record_filter: 'tableIN' + configTables.join(','),
                category: 'ui'
            });
        }
    },

    /**
     * checkUIPolicies(ctx, configTables)
     * UI Policies (sys_ui_policy) for the tables.
     */
    checkUIPolicies: function(ctx, configTables) {
        var tableName = ctx.getTableName();
        var uiPolicies = [];

        try {
            var gr = new GlideRecord('sys_ui_policy');
            this.utils.addInQuery(gr, 'table', configTables);
            gr.query();
            while (gr.next()) {
                var policyTable = ctx.safeGetValue(gr, 'table');
                var name = ctx.safeGetValue(gr, 'short_description') || 'Unnamed';
                var isActive = this.utils.toBool(gr.getValue('active'));
                var polItem = {
                    sys_id: gr.getUniqueValue(),
                    name: name,
                    table: 'sys_ui_policy',
                    trigger_table: policyTable,
                    inherited: (policyTable !== tableName),
                    active: isActive,
                    on_load: ctx.safeGetValue(gr, 'on_load') === 'true',
                    reverse_if_false: ctx.safeGetValue(gr, 'reverse_if_false') === 'true',
                    global: ctx.safeGetValue(gr, 'global') === 'true',
                    sys_created_by: ctx.safeGetValue(gr, 'sys_created_by'),
                    sys_updated_by: ctx.safeGetValue(gr, 'sys_updated_by')
                };

                if (ctx.isIgnoreSncRecordsEnabled && ctx.isIgnoreSncRecordsEnabled() && this.utils.isSncRecord(polItem)) {
                    continue;
                }

                uiPolicies.push(polItem);
            }
        } catch (e) {
            gs.error('FHCheckAutomation.checkUIPolicies error: ' + (e && e.message));
        }

        this.utils.addCategoryAndMetrics(ctx, 'automation', 'ui_policy', 'UI Policies', uiPolicies, {
            table: 'sys_ui_policy',
            icon: 'fa-shield-alt',
            color: '#20c997'
        });

        if (uiPolicies.length > 15) {
            ctx.addIssue('MANY_UI_POLICIES', 'Table has ' + uiPolicies.length + ' active UI Policies. This may slow down form loading.', 'medium', {
                record_table: 'sys_ui_policy',
                record_filter: 'tableIN' + configTables.join(',') + '^active=true',
                category: 'ui'
            });
        }
    },

    /**
     * checkInboundEmailActions(ctx, configTables)
     * Check inbound email actions and flow trigger instances related to email.
     */
    checkInboundEmailActions: function(ctx, configTables) {
        var tableName = ctx.getTableName();
        var inboundEmails = [];

        // sysevent_in_email_action
        try {
            var gr = new GlideRecord('sysevent_in_email_action');
            if (gr.isValid()) {
                this.utils.addInQuery(gr, 'table', configTables);
                gr.query();
                while (gr.next()) {
                    var emailTable = ctx.safeGetValue(gr, 'table');
                    var isActive = this.utils.toBool(gr.getValue('active'));
                    var inItem = {
                        sys_id: gr.getUniqueValue(),
                        name: ctx.safeGetValue(gr, 'name') || 'Unnamed',
                        table: 'sysevent_in_email_action',
                        trigger_table: emailTable,
                        inherited: (emailTable !== tableName),
                        active: isActive,
                        type: ctx.safeGetValue(gr, 'type'),
                        action_type: ctx.safeGetValue(gr, 'action_type'),
                        stop_processing: ctx.safeGetValue(gr, 'stop_processing') === 'true',
                        sys_created_by: ctx.safeGetValue(gr, 'sys_created_by'),
                        sys_updated_by: ctx.safeGetValue(gr, 'sys_updated_by')
                    };

                    if (ctx.isIgnoreSncRecordsEnabled && ctx.isIgnoreSncRecordsEnabled() &&
                        this.utils.isSncRecord && this.utils.isSncRecord(inItem)) {
                        continue;
                    }

                    inboundEmails.push(inItem);

                    if (!isActive) {
                        this.utils.addIssueUnlessSnc(ctx, 'INACTIVE_INBOUND_EMAIL',
                            'Inactive inbound email action: "' + (ctx.safeGetValue(gr, 'name') || 'Unnamed') + '"', 'low', {
                                record_table: 'sysevent_in_email_action',
                                record_sys_id: gr.getUniqueValue(),
                                category: 'automation'
                            }, inItem);
                    }
                }
            }
        } catch (e) {
            gs.error('FHCheckAutomation.checkInboundEmailActions (sysevent_in_email_action) error: ' + (e && e.message));
        }

        // sys_hub_trigger_instance (flow triggers including email)
        try {
            // For each configured table we look for trigger instances that reference it
            for (var i = 0; i < configTables.length; i++) {
                var tableToCheck = configTables[i];
                var trigGr = new GlideRecord('sys_hub_trigger_instance');
                if (!trigGr.isValid()) continue;
                trigGr.addQuery('trigger_type', 'IS', 'email');
                // Many trigger instances store trigger_config or caches - search in trigger_config
                trigGr.addQuery('trigger_config', 'LIKE', tableToCheck);
                trigGr.query();
                while (trigGr.next()) {
                    var triggerConfig = ctx.safeGetValue(trigGr, 'trigger_config') || '';
                    var related = false;
                    for (var t = 0; t < configTables.length; t++) {
                        if (triggerConfig.indexOf(configTables[t]) > -1) {
                            related = true;
                            break;
                        }
                    }
                    if (!related) continue;

                    var flowRef = trigGr.getValue('flow');
                    var flowName = '';
                    if (flowRef) {
                        var flowRecord = this.utils.getRecordBySysId('sys_hub_flow', flowRef, ['name']);
                        if (flowRecord) flowName = flowRecord.name || '';
                    }

                    inboundEmails.push({
                        sys_id: trigGr.getUniqueValue(),
                        name: flowName || 'Flow Trigger',
                        table: 'sys_hub_trigger_instance',
                        trigger_table: tableToCheck,
                        inherited: false,
                        active: true,
                        type: 'flow_trigger',
                        action_type: 'email',
                        stop_processing: false
                    });
                }
            }
        } catch (e) {
            gs.error('FHCheckAutomation.checkInboundEmailActions (trigger instances) error: ' + (e && e.message));
        }

        this.utils.addCategoryAndMetrics(ctx, 'automation', 'inbound_email', 'Inbound Email Actions', inboundEmails, {
            table: 'sysevent_in_email_action',
            icon: 'fa-inbox',
            color: '#e83e8c'
        });

        if (inboundEmails.length > 10) {
            ctx.addIssue('MANY_INBOUND_EMAILS', 'Table has ' + inboundEmails.length + ' inbound email actions. Review for efficiency.', 'low', {
                record_table: 'sysevent_in_email_action',
                record_filter: 'target_tableIN' + configTables.join(','),
                category: 'automation'
            });
        }
    },

    type: 'FHCheckAutomation'
};
