sys_script_include.script:
var FHCheckTable = Class.create();
FHCheckTable.prototype = {
    initialize: function() {
        this.utils = new FHScanUtils();
        this.optionsHandler = new FHOptionsHandler();
    },

    /**
     * execute(ctx)
     * Primary public entrypoint to execute automation checks
     * @param {Object} ctx - FHAnalysisContext
     */
    execute: function(ctx) {
        if (!ctx) return;

        // Register table category first
        ctx.registerCategory('table', 'Table', 'fa-table');

        // Get tables to check from centralized context logic
        var tablesToCheck = ctx.getTablesToCheck();

        // Run individual checks 
        this._checkTableSummary(ctx, tablesToCheck);
        this._checkFieldUsage(ctx, tablesToCheck);
        this._checkReferenceFields(ctx, tablesToCheck);
        this._checkBusinessRules(ctx, tablesToCheck);
        this._checkClientScripts(ctx, tablesToCheck);
        this._checkCustomFields(ctx, tablesToCheck);

        if (ctx.isDeepScanEnabled())
            this.optionsHandler.deepScanScripts(ctx);

        if (ctx.isAnalyzeReferencesEnabled())
            this.optionsHandler.analyzeIntegrationDependencies(ctx);

        if (ctx.isLdapEnabled())
            this.optionsHandler.analyzeLdapConfiguration(ctx);

    },

    /**
     * Check table existence + record count, raise issues,
     * and add a "Table Summary" item in the 'table' category for UI.
     * Uses categories structure only, no metrics.
     */
    _checkTableSummary: function(ctx, configTables) {
        //var tableName = ctx.getTableName();
        var tableSummary = [];
        var tableLabel = null;
        var recordCount = 0;

        try {
            var ga = new GlideAggregate(configTables);
            ga.addAggregate('COUNT');
            ga.query();
            if (ga.next()) {
                recordCount = parseInt(ga.getAggregate('COUNT'), 10) || 0;
            } else {
                recordCount = 0;
            }
        } catch (e) {
            gs.error('FHCheckTable._checkTableSummary record count error for ' + configTables + ': ' + e);
            recordCount = 0;
        }

        if (recordCount === 0) {
            ctx.addIssue('EMPTY_TABLE', 'Table "' + configTables + '" contains no records.', 'low', {
                record_table: configTables
            });
        } else if (recordCount > 100000) {
            ctx.addIssue('LARGE_TABLE', 'Table "' + configTables + '" has ' + configTables + ' records — consider archiving.', 'medium', {
                record_table: configTables
            });
        }


        tableSummary.push({
            table: configTables,
            label: tableLabel,
            record_count: recordCount,

        });

        this.utils.addCategoryAndMetrics(ctx, 'table', 'table_summary', 'Table Summary', tableSummary, {
            table: 'sys_db_object',
            icon: 'fa-table',
            color: '#17a2b8',
        });
    },

    /**
     * Check field usage / custom fields (u_), mandatory, types
     */
    _checkFieldUsage: function(ctx,configTables) {
        var tableName = ctx.getTableName();
        var fields = this.utils.getRecords('sys_dictionary', function(gr) {
            gr.addQuery('name', configTables);
            gr.addQuery('internal_type', '!=', 'collection'); // exclure l’entrée collection
        }, ['element', 'column_label', 'internal_type', 'mandatory', 'max_length', 'default_value'], 0, 'dict:' + configTables);

        var customFields = [];
        var summary = {
            table: configTables,
            total_count: fields.length,
            custom_count: 0,
            mandatory_count: 0,
            max_length_over_255: 0
        };

        for (var i = 0; i < fields.length; i++) {
            var f = fields[i];
            var isCustom = (f.element || '').indexOf('u_') === 0;
            var isMandatory = (f.mandatory === 'true' || f.mandatory === true);
            var maxLen = parseInt(f.max_length, 10) || 0;

            if (isCustom) {
                customFields.push({
                    sys_id: f.sys_id,
                    name: f.element,
                    label: f.column_label,
                    type: f.internal_type,
                    mandatory: isMandatory,
                    max_length: maxLen,
                    table: 'sys_dictionary'
                });
                summary.custom_count++;
            }
            if (isMandatory) summary.mandatory_count++;
            if (maxLen > 255) summary.max_length_over_255++;
        }

        this.utils.addCategoryAndMetrics(ctx, 'table', 'fields_summary', 'Fields Summary', [summary], {
            table: 'sys_dictionary',
            icon: 'fa-list',
            color: '#6c757d'
        });

        if (customFields.length > 0) {
            this.utils.addCategoryAndMetrics(ctx, 'table', 'custom_fields', 'Custom Fields', customFields, {
                table: 'sys_dictionary',
                icon: 'fa-columns',
                color: '#17a2b8'
            });
        }

        if (customFields.length > 50) {
            ctx.addIssue('MANY_CUSTOM_FIELDS', 'Table has ' + customFields.length + ' custom fields (u_).', 'medium', {
                record_table: 'sys_dictionary',
                record_filter: 'name=' + configTables + '^elementSTARTSWITHu_',
                category: 'table'
            });
        }
        if (summary.max_length_over_255 > 0) {
            ctx.addIssue('LONG_TEXT_FIELDS', summary.max_length_over_255 + ' fields with max_length > 255.', 'low', {
                record_table: 'sys_dictionary',
                record_filter: 'name=' + configTables + '^max_length>255',
                category: 'table'
            });
        }
    },

    /**
     * Check reference fields (dictionary.reference not empty)
     */
    _checkReferenceFields: function(ctx,configTables) {

        var tableName = ctx.getTableName();
        var refs = this.utils.getRecords('sys_dictionary', function(gr) {
            gr.addQuery('name', configTables);
            gr.addNotNullQuery('reference');
        }, ['element', 'column_label', 'reference', 'reference_qual'], 0, 'dictref:' + configTables);

        var refItems = [];
        for (var i = 0; i < refs.length; i++) {
            refItems.push({
                sys_id: refs[i].sys_id,
                name: refs[i].element,
                label: refs[i].column_label,
                reference: refs[i].reference,
                reference_qual: refs[i].reference_qual,
                table: 'sys_dictionary'
            });
        }

        this.utils.addCategoryAndMetrics(ctx, 'table', 'reference_fields', 'Reference Fields', refItems, {
            table: 'sys_dictionary',
            icon: 'fa-link',
            color: '#6f42c1'
        });

        if (refItems.length === 0) {
            ctx.addIssue('NO_REFERENCE_FIELDS', 'No reference fields found on table.', 'low', {
                record_table: 'sys_dictionary',
                record_filter: 'name=' + configTables + '^referenceISNOTEMPTY',
                category: 'table'
            });
        }
    },

    _checkBusinessRules: function(ctx,configTables) {
        var tableName = ctx.getTableName();
        var tablesToCheck = ctx.getTablesToCheck();
        var businessRules = [];

        try {
            var gr = new GlideRecord('sys_script');
            this.utils.addInQuery(gr, 'collection', tablesToCheck);
            gr.query();
            while (gr.next()) {
                var brName = ctx.safeGetValue(gr, 'name') || 'Unnamed';
                var brTable = ctx.safeGetValue(gr, 'collection');
                var isActive = this.utils.toBool(gr.getValue('active'));
                businessRules.push({
                    sys_id: gr.getUniqueValue(),
                    name: brName,
                    table: 'sys_script',
                    collection: brTable,
                    active: isActive,
                    inherited: (brTable !== configTables),
                    when: ctx.safeGetValue(gr, 'when'),
                    order: ctx.safeGetValue(gr, 'order')
                });

                if (!isActive) {
                    ctx.addIssue('INACTIVE_BUSINESS_RULE', 'Inactive business rule: "' + brName + '"', 'low', {
                        record_table: 'sys_script',
                        record_sys_id: gr.getUniqueValue(),
                        category: 'automation'
                    });
                }
            }

            this.utils.addCategoryAndMetrics(ctx, 'automation', 'business_rules', 'Business Rules', businessRules, {
                table: 'sys_script',
                icon: 'fa-cogs',
                color: '#6c757d'
            });

            var activeCount = businessRules.filter(function(br) {
                return br.active;
            }).length;
            if (activeCount > 50) {
                ctx.addIssue('MANY_BUSINESS_RULES', 'Table has ' + businessRules.length + ' business rules, ' + activeCount + ' active. This may impact performance.', 'medium', {
                    record_table: 'sys_script',
                    record_filter: 'collection=' + configTables + '^active=true',
                    category: 'automation'
                });
            }
        } catch (e) {
            gs.error('FHCheckTable._checkBusinessRules error: ' + e);
        }
    },

    _checkClientScripts: function(ctx,configTables) {
        var tableName = ctx.getTableName();
        var tablesToCheck = ctx.getTablesToCheck();
        var clientScripts = [];

        try {
            var gr = new GlideRecord('sys_script_client');
            this.utils.addInQuery(gr, 'table', tablesToCheck);
            gr.addQuery('active', true);
            gr.query();
            while (gr.next()) {
                clientScripts.push({
                    sys_id: gr.getUniqueValue(),
                    name: ctx.safeGetValue(gr, 'name') || 'Unnamed',
                    table: 'sys_script_client',
                    target_table: ctx.safeGetValue(gr, 'table'),
                    type: ctx.safeGetValue(gr, 'type'),
                    ui_type: ctx.safeGetValue(gr, 'ui_type'),
                    active: true,
                    inherited: (ctx.safeGetValue(gr, 'table') !== configTables)
                });
            }

            this.utils.addCategoryAndMetrics(ctx, 'automation', 'client_scripts', 'Client Scripts', clientScripts, {
                table: 'sys_script_client',
                icon: 'fa-desktop',
                color: '#6c757d'
            });

            if (clientScripts.length > 15) {
                ctx.addIssue('MANY_CLIENT_SCRIPTS', 'Table has ' + clientScripts.length + ' active client scripts. This may impact user experience.', 'medium', {
                    record_table: 'sys_script_client',
                    record_filter: 'table=' + configTables + '^active=true',
                    category: 'automation'
                });
            }
        } catch (e) {
            gs.error('FHCheckTable._checkClientScripts query error: ' + e);
        }
    },

    /**
     * _checkCustomFields(ctx)
     * Custom fields (prefix u_)
     */
    _checkCustomFields: function(ctx,configTables) {
        var tableName = ctx.getTableName();
        var customFields = [];
        var gr = new GlideRecord('sys_dictionary');
        gr.addQuery('name', configTables);
        gr.addQuery('internal_type', '!=', 'collection');
        gr.addQuery('element', 'STARTSWITH', 'u_');
        gr.query();
        while (gr.next()) {
            customFields.push({
                sys_id: gr.getUniqueValue(),
                name: gr.getValue('element'),
                label: ctx.safeGetValue(gr, 'column_label') || ctx.safeGetValue(gr, 'label'),
                type: ctx.safeGetValue(gr, 'internal_type'),
                mandatory: (ctx.safeGetValue(gr, 'mandatory') === 'true'),
                table: 'sys_dictionary'
            });
        }

        this.utils.addCategoryAndMetrics(ctx, 'table', 'custom_fields', 'Custom Fields', customFields, {
            table: 'sys_dictionary',
            icon: 'fa-columns',
            color: '#17a2b8'
        });

        if (customFields.length > 50) {
            ctx.addIssue('MANY_CUSTOM_FIELDS', 'Table has ' + customFields.length + ' custom fields (u_).', 'medium', {
                record_table: 'sys_dictionary',
                record_filter: 'name=' + configTables + '^elementSTARTSWITHu_',
                category: 'table'
            });
        }
    },

    type: 'FHCheckTable'

};
