sys_script_include.script:
/**
 * FHAnalysisContext - Context for analysis execution
 * 
 * Centralizes:
 * - Configuration options management
 * - Table hierarchy utilities
 * - Issue tracking
 * - Metrics collection
 */
var FHAnalysisContext = Class.create();
FHAnalysisContext.prototype = {
    initialize: function(config) {
        this.config = config;
        this.issues = [];

        // Initialize options with defaults
        this.options = {
            deep_scan: this._isTrue(config.deep_scan),
            include_children_tables: this._isTrue(config.include_children_tables),
            analyze_references: this._isTrue(config.analyze_references),
            include_ldap: this._isTrue(config.include_ldap),
            ignore_servicenow_records: this._isTrue(config.ignore_servicenow_records)
        };
					gs.info('options '+ JSON.stringify(config));


        // Initialize categories structure for standardized output
        this.categories = {};

        // Store execution metadata (duration, etc.) - minimal, no metrics structure
        this._executionMetadata = {
            duration_ms: 0
        };

        // Cache for table hierarchy (computed once, reused)
        this._tableHierarchyCache = null;
        this._childrenTablesCache = null;
        this._tablesToCheckCache = null;
    },

    /**
     * Check if a value is truthy (handles 'true', '1', true, 1)
     * @private
     */
    _isTrue: function(val) {
        return val === 'true' || val === '1' || val === true || val === 1;
    },

    // ==================== OPTIONS HELPERS ====================

    /**
     * Check if deep scan is enabled
     * @returns {Boolean}
     */
    isDeepScanEnabled: function() {
        return this.options.deep_scan === true;
    },

    /**
     * Check if children tables should be included
     * @returns {Boolean}
     */
    isIncludeChildrenEnabled: function() {
        return this.options.include_children_tables === true;
    },

    /**
     * Check if reference analysis is enabled
     * @returns {Boolean}
     */
    isAnalyzeReferencesEnabled: function() {
        return this.options.analyze_references === true;
    },

    /**
     * Check if LDAP analysis is enabled
     * @returns {Boolean}
     */
    isLdapEnabled: function() {
        return this.options.include_ldap === true;
    },

    /**
     * Check if ServiceNow base records should be ignored (created/admin or glide.maint and never updated by others)
     * @returns {Boolean}
     */
    isIgnoreSncRecordsEnabled: function() {
        return this.options.ignore_servicenow_records === true;
    },

    // ==================== TABLE HIERARCHY HELPERS ====================

    /**
     * Get table name from config
     * @returns {String} Table name
     */
    getTableName: function() {
        return this.config && this.config.table_name ? String(this.config.table_name) : '';
    },

    /**
     * Get table hierarchy (current table + all parent tables)
     * Results are cached for performance
     * @returns {Array} Array of table names [current, parent1, parent2, ...]
     */
    getTableHierarchy: function() {
        if (this._tableHierarchyCache !== null) {
            return this._tableHierarchyCache;
        }

        var tableName = this.getTableName();
        var tables = [tableName];

        if (!tableName) {
            this._tableHierarchyCache = tables;
            return tables;
        }

        try {
            var gr = new GlideRecord('sys_db_object');
            if (gr.get('name', tableName)) {
                var superClass = gr.super_class;
                while (superClass && !superClass.nil()) {
                    var parentGr = superClass.getRefRecord();
                    if (parentGr) {
                        var parentName = parentGr.getValue('name');
                        if (parentName && tables.indexOf(parentName) === -1) {
                            tables.push(parentName);
                        }
                        superClass = parentGr.super_class;
                    } else {
                        break;
                    }
                }
            }
        } catch (e) {
        }

        this._tableHierarchyCache = tables;
        return tables;
    },

    /**
     * Get children tables (tables that extend the current table)
     * Results are cached for performance
     * @returns {Array} Array of child table names
     */
    getChildrenTables: function() {
        if (this._childrenTablesCache !== null) {
            return this._childrenTablesCache;
        }

        var tableName = this.getTableName();
        var children = [];

        if (!tableName) {
            this._childrenTablesCache = children;
            return children;
        }

        try {
            var parentGr = new GlideRecord('sys_db_object');
            if (parentGr.get('name', tableName)) {
                var parentSysId = parentGr.getUniqueValue();

                var gr = new GlideRecord('sys_db_object');
                gr.addQuery('super_class', parentSysId);
                gr.query();

                while (gr.next()) {
                    var childName = gr.getValue('name');
                    if (childName && children.indexOf(childName) === -1) {
                        children.push(childName);
                    }
                }
            }
        } catch (e) {
            // Ignore errors
        }

        this._childrenTablesCache = children;
        return children;
    },

    /**
     * Get all tables to check based on options
     * Combines: current table + parents + children (if option enabled)
     * Results are cached for performance
     * @returns {Array} Array of table names to check
     */
    getTablesToCheck: function() {
        if (this._tablesToCheckCache !== null)
            return this._tablesToCheckCache;

        var tables = this.getTableHierarchy().slice();

        if (this.isIncludeChildrenEnabled()) {
            var children = this.getChildrenTables();
            children.forEach(function(child) {
                if (tables.indexOf(child) === -1) {
                    tables.push(child);
                }
            });
        }

        this._tablesToCheckCache = tables;
        return tables;
    },

    // ==================== UTILITY HELPERS ====================

    /**
     * Safely get a value from a GlideRecord, returning empty string if null
     * @param {GlideRecord} gr - GlideRecord
     * @param {String} field - Field name
     * @returns {String} Field value or empty string
     */
    safeGetValue: function(gr, field) {
        var val = gr.getValue(field);
        return (val === null || val === undefined) ? '' : String(val);
    },

    /**
     * Check if a script contains an exact table reference
     * @param {String} script - Script content
     * @param {String} tableName - Table name to search
     * @returns {Boolean} True if exact match found
     */
    scriptContainsTable: function(script, tableName) {
        if (!script || !tableName) return false;

        var patterns = [
            new RegExp("GlideRecord\\s*\\(\\s*['\"]" + tableName + "['\"]\\s*\\)", "i"),
            new RegExp("GlideAggregate\\s*\\(\\s*['\"]" + tableName + "['\"]\\s*\\)", "i"),
            new RegExp("table\\s*[=:]\\s*['\"]" + tableName + "['\"]", "i"),
            new RegExp("setTableName\\s*\\(\\s*['\"]" + tableName + "['\"]\\s*\\)", "i")
        ];

        for (var i = 0; i < patterns.length; i++) {
            if (patterns[i].test(script)) {
                return true;
            }
        }

        return false;
    },

    /**
     * Check if a script updates a specific field
     * @param {String} script - Script content
     * @param {String} fieldName - Field name to check
     * @returns {Boolean} True if script updates the field
     */
    scriptUpdatesField: function(script, fieldName) {
        if (!script || !fieldName) return false;

        var patterns = [
            new RegExp("\\b(current|gr|record|glideRecord)\\." + fieldName + "\\s*=", "i"),
            new RegExp("setValue\\s*\\(\\s*['\"]" + fieldName + "['\"]", "i")
        ];

        for (var i = 0; i < patterns.length; i++) {
            if (patterns[i].test(script)) {
                return true;
            }
        }

        return false;
    },

    // ==================== CATEGORY MANAGEMENT ====================

    /**
     * Register a category for structured output
     * @param {String} categoryId - Category identifier (e.g., 'automation', 'integration')
     * @param {String} label - Display label
     * @param {String} icon - FontAwesome icon class (e.g., 'fa-cogs')
     */
    registerCategory: function(categoryId, label, icon) {
        if (!this.categories[categoryId]) {
            this.categories[categoryId] = {
                id: categoryId,
                label: label,
                icon: icon || 'fa-folder',
                items: []
            };
        }
    },

    /**
     * Add items to a category
     * @param {String} categoryId - Category identifier
     * @param {String} itemType - Item type (e.g., 'scheduled_job', 'flow')
     * @param {String} itemLabel - Display label for this item type
     * @param {Array} records - Array of record objects
     * @param {Object} options - Additional options (table, icon, color)
     */
    addCategoryItems: function(categoryId, itemType, itemLabel, records, options) {
        // Auto-register category if not exists
        if (!this.categories[categoryId]) {
            this.registerCategory(categoryId, categoryId, 'fa-folder');
        }

        options = options || {};
        var activeCount = 0;
        var inactiveCount = 0;
        var directCount = 0;
        var inheritedCount = 0;

        // Calculate counts
        records.forEach(function(r) {
            if (r.active === true || r.active === 'true') activeCount++;
            else inactiveCount++;
            if (r.inherited) inheritedCount++;
            else directCount++;
        });

        this.categories[categoryId].items.push({
            type: itemType,
            label: itemLabel,
            table: options.table || '',
            icon: options.icon || 'fa-file',
            color: options.color || '#6c757d',
            records: records,
            count: records.length,
            active_count: activeCount,
            inactive_count: inactiveCount,
            direct_count: directCount,
            inherited_count: inheritedCount
        });
    },

    /**
     * Get a category by ID
     * @param {String} categoryId - Category identifier
     * @returns {Object} Category object or null
     */
    getCategory: function(categoryId) {
        return this.categories[categoryId] || null;
    },

    /**
     * Get all categories
     * @returns {Object} All categories
     */
    getCategories: function() {
        return this.categories;
    },

    // ==================== ISSUE MANAGEMENT ====================

    /**
     * Add an issue to the context
     * @param {String} code - Issue code
     * @param {String} message - Issue message
     * @param {String} severity - Issue severity (low, medium, high)
     * @param {Object} metadata - Optional metadata (record_table, record_sys_id, record_filter, category)
     */
    addIssue: function(code, message, severity, metadata) {
        var issue = {
            code: code,
            message: message,
            severity: severity || 'low',
            timestamp: new Date().toISOString()
        };

        // Add metadata for record linking
        if (metadata) {
            if (metadata.record_table) issue.record_table = metadata.record_table;
            if (metadata.record_sys_id) issue.record_sys_id = metadata.record_sys_id;
            if (metadata.record_filter) issue.record_filter = metadata.record_filter;
            if (metadata.category) issue.category = metadata.category;
        }

        this.issues.push(issue);
    },

    // ==================== RESULT GENERATION ====================

    /**
     * Helper to check if table exists (from categories.table)
     * @returns {Boolean} True if table exists
     */
    isTableExists: function() {
        var tableCategory = this.getCategory('table');
        if (tableCategory && tableCategory.items && tableCategory.items.length > 0) {
            for (var i = 0; i < tableCategory.items.length; i++) {
                var item = tableCategory.items[i];
                if (item.type === 'table_summary' && item.records && item.records.length > 0) {
                    return item.records[0].exists === true;
                }
            }
        }
        return false;
    },

    /**
     * Get execution metadata (duration, etc.)
     * @returns {Object} Execution metadata
     */
    getExecutionMetadata: function() {
        return this._executionMetadata;
    },

    /**
     * Set execution duration
     * @param {Number} durationMs - Duration in milliseconds
     */
    setDuration: function(durationMs) {
        this._executionMetadata.duration_ms = durationMs;
    },

    /**
     * Convert context to result object
     * @returns {Object} Result object
     */
    toResult: function() {
        var self = this;
        return {
            table: this.config.table_name,
            issues: this.issues,
            categories: this.categories,
            execution_metadata: this._executionMetadata,
            hasBlockingIssues: function() {
                return self.issues.some(function(issue) {
                    return issue.severity === 'high';
                });
            }
        };
    },

    type: 'FHAnalysisContext'
};
