sys_script_include.script:
/**
 * FHAnalyzer - Main analysis engine for Foundation Health Assessment
 * This script include contains all core logic for table analysis
 */
var FHAnalyzer = Class.create();
FHAnalyzer.prototype = {
    initialize: function() {
        this.resultsTable = 'x_1310794_founda_0_results';
        this.configTable = 'x_1310794_founda_0_configurations';
    },

    /**
     * Run analysis for a specific configuration
     * @param {String} configSysId - Configuration sys_id
     * @returns {Object} Analysis result
     */
    runAnalysis: function(configSysId) {
        var config = this.getConfiguration(configSysId);
        var engineResult = new FHAnalysisEngine().runVerification(config);
        var structuredResult = this._buildResultStructure(config, engineResult);
        resultSysId = this._saveResult(structuredResult);
        return {
            success: true,
            analysis_id: resultSysId,
            sys_id: resultSysId,
            details: structuredResult
        };
    },

    getConfiguration: function(configSysId) {
        var gr = new GlideRecord('x_1310794_founda_0_configurations');
        if (!gr.get(configSysId)) {
            gs.error('[FHAnalyzer] Configuration not found: ' + configSysId);
            return null;
        }

        gs.info('[FHAnalyzer] Loading configuration: ' + gr.getValue('name'));
        gs.info('[FHAnalyzer]   Table: ' + gr.table.name);
        gs.info('[FHAnalyzer]   use_template: ' + gr.getValue('use_template'));

        var out = [];
        var viIds = '';

        // Load VIs from template or direct selection
        if (gr.use_template && gr.getValue('template')) {
            gs.info('[FHAnalyzer]   Mode: TEMPLATE');
            var template = new GlideRecord('x_1310794_founda_0_analysis_templates');
            if (template.get(gr.getValue('template'))) {
                viIds = template.getValue('verification_items') || '';
                gs.info('[FHAnalyzer]   Template: ' + template.getValue('name'));
                gs.info('[FHAnalyzer]   VI IDs: ' + viIds);
            } else {
                gs.error('[FHAnalyzer]   Template not found: ' + gr.getValue('template'));
            }
        } else {
            gs.info('[FHAnalyzer]   Mode: DIRECT');
            viIds = gr.getValue('verification_items') || '';
            gs.info('[FHAnalyzer]   VI IDs: ' + viIds);
        }

        if (!viIds) {
            gs.warn('[FHAnalyzer]   No verification items found, returning empty config');
            return this._buildEmptyConfig(gr);
        }

        var verificationItems = new GlideRecord('x_1310794_founda_0_verification_items');
        verificationItems.addQuery('sys_id', 'IN', viIds);
        verificationItems.addQuery('active', true);
        verificationItems.query();

        var viCount = verificationItems.getRowCount();
        gs.info('[FHAnalyzer]   Found ' + viCount + ' active VIs');

        while (verificationItems.next()) {
            var viSysId = verificationItems.sys_id.toString();
            var viTableName = verificationItems.table.name + '' || '';

            // Find issue rules that reference this VI
            var issueRuleIds = this._getIssueRulesForVI(viSysId);

            var itemObj = {
                sys_id: viSysId,
                name: verificationItems.name.toString(),
                active: Boolean(verificationItems.active),
                category: verificationItems.category + '',
                tableSysid: verificationItems.table + '',
                tableName: viTableName,
                query_type: verificationItems.query_type + '',
                fields: verificationItems.fields + '' || '',
                metadata: verificationItems.metadata + '' || '',
                query_value: verificationItems.query_value + '',
                query_script: verificationItems.query_script + '',
                issue_rules: issueRuleIds
            };

            gs.info('[FHAnalyzer]   VI: ' + itemObj.name);
            gs.info('[FHAnalyzer]     - table: ' + viTableName);
            gs.info('[FHAnalyzer]     - query_value: ' + itemObj.query_value);
            gs.info('[FHAnalyzer]     - issue_rules: ' + itemObj.issue_rules);

            itemObj = this._ensureMetadataDefaults(itemObj);
            out.push(itemObj);
        }

        return {
            sys_id: gr.sys_id.toString(),
            name: gr.name.toString(),
            active: Boolean(gr.active),
            table_name: gr.table.name || '',
            table_label: gr.table.label || '',
            table_sys_id: gr.table.sys_id || '',
            description: gr.description || '',
            include_children_tables: gr.include_children_tables,
            deep_scan: gr.deep_scan,
            ignore_servicenow_records: gr.ignore_servicenow_records,
            include_ldap: gr.include_ldap,
            verification_items: out
        };
    },

    _buildEmptyConfig: function(gr) {
        return {
            sys_id: gr.sys_id.toString(),
            name: gr.name.toString(),
            active: Boolean(gr.active),
            table_name: gr.table.name || '',
            table_label: gr.table.label || '',
            table_sys_id: gr.table.sys_id || '',
            description: gr.description || '',
            include_children_tables: gr.include_children_tables,
            deep_scan: gr.deep_scan,
            ignore_servicenow_records: gr.ignore_servicenow_records,
            include_ldap: gr.include_ldap,
            verification_items: []
        };
    },

    /**
     * Get issue rules for a verification item
     * @param {String} viSysId - Verification item sys_id
     * @returns {String} Comma-separated rule IDs
     */
    _getIssueRulesForVI: function(viSysId) {
        var ruleIds = [];
        var rules = new GlideRecord('x_1310794_founda_0_issue_rules');
        rules.addQuery('verification_items', 'CONTAINS', viSysId);
        rules.addQuery('active', true);
        rules.query();

        while (rules.next()) {
            ruleIds.push(rules.getUniqueValue());
        }

        return ruleIds.join(',');
    },

    _ensureMetadataDefaults: function(item) {
        var meta = {};
        try {
            if (item.metadata) {
                meta = JSON.parse(item.metadata);
            }
        } catch (e) {
            // fallback if metadata invalid
            meta = {};
        }

        var key = item.name || item.tableName || item.category || 'item';
        var palette = ['#0c63d4', '#6f42c1', '#d97706', '#0f766e', '#be185d', '#4338ca', '#2563eb', '#dc2626', '#16a34a'];
        var icons = ['fa-clock', 'fa-cog', 'fa-plug', 'fa-shield', 'fa-code', 'fa-database', 'fa-bolt', 'fa-magic', 'fa-tags'];
        var hash = 0;
        for (var i = 0; i < key.length; i++) {
            hash = (hash + key.charCodeAt(i)) % 997;
        }
        if (!meta.record_type) meta.record_type = item.tableName || item.tableSysid || 'record';
        if (!meta.displayName) meta.displayName = item.name || item.tableName || (item.category + ' item');
        if (!meta.icon) meta.icon = icons[hash % icons.length];
        if (!meta.color) meta.color = palette[hash % palette.length];

        item.metadata = JSON.stringify(meta);
        return item;
    },

    /**
     * Build structured result object
     * @private
     * @param {Object} config - Analysis configuration
     * @param {Object} result - Analysis result from engine
     * @returns {Object} Structured result
     */
    _buildResultStructure: function(config, result) {
        return {
            configuration: {
                sys_id: config.sys_id,
                name: config.name,
                table_name: config.table_name,
                table_label: config.table_label,
                description: config.description,
                options: {
                    deep_scan: config.deep_scan,
                    include_children_tables: config.include_children_tables,
                    ignore_servicenow_records: config.ignore_servicenow_records,
                    include_ldap: config.include_ldap
                },
                verification_items: (config.verification_items || []).map(function(vi) {
                    return {
                        sys_id: vi.sys_id,
                        name: vi.name,
                        category: vi.category,
                        table_name: vi.tableName,
                        query_type: vi.query_type,
                        query_value: vi.query_value,
                        issue_rules: vi.issue_rules
                    };
                })
            },
            result: {
                success: result.success,
                health_score: result.health_score || 0,
                issues_high: result.issues_high || 0,
                issues_medium: result.issues_medium || 0,
                issues_low: result.issues_low || 0,
                issue_count: result.issue_count || 0,
                record_count: result.record_count || 0,
                issues: result.issues || [],
                errors: result.errors || []
            }
        };
    },

    /**
     * Save analysis result to database
     * @private
     * @param {Object} structuredResult - Structured result object
     * @returns {String} Result sys_id
     */
    _saveResult: function(structuredResult) {
        var gr = new GlideRecord(this.resultsTable);
        gr.initialize();
        gr.setValue('state', 'Completed');
        gr.setValue('details', JSON.stringify(structuredResult));
        return gr.insert();
    },

    type: 'FHAnalyzer'
};
