sys_ws_operation.operation_script:
(function process(request, response) {
    try {
        var tableName = request.queryParams.table_name;

        if (!tableName) {
            response.setStatus(400);
            response.setBody({
                success: false,
                error: 'table_name query parameter is required'
            });
            return;
        }

        // Verify table exists
        var tableGr = new GlideRecord('sys_db_object');
        tableGr.addQuery('name', tableName);
        tableGr.query();

        if (!tableGr.next()) {
            response.setStatus(404);
            response.setBody({
                success: false,
                error: 'Table not found: ' + tableName
            });
            return;
        }

        // Get custom fields (fields starting with u_ or in custom scope)
        var fields = [];
        var dictGr = new GlideRecord('sys_dictionary');
        dictGr.addQuery('name', tableName);
        dictGr.addQuery('element', 'STARTSWITH', 'u_');
        dictGr.addQuery('internal_type', '!=', 'collection');
        dictGr.orderBy('element');
        dictGr.query();

        while (dictGr.next()) {
            var fieldName = dictGr.getValue('element');
            var fieldLabel = dictGr.getValue('column_label') || fieldName;
            var fieldType = dictGr.getValue('internal_type');

            // Calculate fill rate
            var totalRecords = 0;
            var filledRecords = 0;

            var countGa = new GlideAggregate(tableName);
            countGa.addAggregate('COUNT');
            countGa.query();
            if (countGa.next()) {
                totalRecords = parseInt(countGa.getAggregate('COUNT'), 10) || 0;
            }

            if (totalRecords > 0) {
                var filledGa = new GlideAggregate(tableName);
                filledGa.addNotNullQuery(fieldName);
                filledGa.addQuery(fieldName, '!=', '');
                filledGa.addAggregate('COUNT');
                filledGa.query();
                if (filledGa.next()) {
                    filledRecords = parseInt(filledGa.getAggregate('COUNT'), 10) || 0;
                }
            }

            var fillRate = totalRecords > 0 ? Math.round((filledRecords / totalRecords) * 100) : 0;

            fields.push({
                name: fieldName,
                label: fieldLabel,
                type: fieldType,
                fill_rate: fillRate,
                filled_records: filledRecords,
                total_records: totalRecords,
                sys_id: dictGr.getUniqueValue()
            });
        }

        response.setStatus(200);
        response.setBody({
            success: true,
            table_name: tableName,
            table_label: tableGr.getValue('label') || tableName,
            count: fields.length,
            fields: fields
        });

    } catch (error) {
        response.setStatus(500);
        response.setBody({
            success: false,
            error: error.message
        });
    }
})(request, response);

sys_ws_operation.request_example:
// Query Parameter: table_name (required)
// Example: /api/x_1310794_founda_0/fha/fields?table_name=incident

// Success Response (200):
{
  "success": true,
  "table_name": "incident",
  "table_label": "Incident",
  "count": 5,
  "fields": [
    {
      "name": "u_custom_field",
      "label": "Custom Field",
      "type": "string",
      "fill_rate": 85,
      "filled_records": 850,
      "total_records": 1000,
      "sys_id": "abc123..."
    }
  ]
}

// Error Response (404):
{
  "success": false,
  "error": "Table not found: my_table"
}
