sys_ws_operation.operation_script:
(function process(request, response) {
    try {
        var analysisId = request.pathParams.analysis_id;

        if (!analysisId) {
            response.setStatus(400);
            response.setBody({
                success: false,
                error: 'Analysis ID is required'
            });
            return;
        }

        var analyzer = new x_1310794_founda_0.FHAnalyzer();
        var result = analyzer.getAnalysisResult(analysisId);

        if (!result || !result.success) {
            response.setStatus(404);
            response.setBody({
                success: false,
                error: result ? result.error : 'Analysis not found'
            });
            return;
        }

        response.setStatus(200);
        response.setBody({
            success: true,
            analysis: {
                sys_id: analysisId,
                table_name: result.result.table_name,
                health_score: result.result.health_score,
                issues_count: result.result.issue_found,
                status: result.result.status,
                created_on: result.result.created_on,
                issues: result.result.issues || [],
                metrics: result.result.metrics || {}
            }
        });
    } catch (error) {
        response.setStatus(500);
        response.setBody({
            success: false,
            error: error.message
        });
    }
})(request, response);
