sys_ws_operation.operation_script:
(function process(request, response) {
    try {
        var analyzer = new x_1310794_founda_0.FHAnalyzer();
        var tables = analyzer.getAvailableAnalyses();

        response.setStatus(200);
        response.setBody({
            success: true,
            count: tables.length,
            tables: tables
        });
    } catch (error) {
        response.setStatus(500);
        response.setBody({
            success: false,
            error: error.message
        });
    }
})(request, response);

sys_ws_operation.request_example:
// No request body required for GET request

// Example Response:
{
  "success": true,
  "count": 3,
  "tables": [
    {
      "config_sys_id": "abc123...",
      "display_name": "Incident Analysis",
      "table_name": "incident",
      "table_label": "Incident",
      "description": "Analysis configuration for incident table"
    }
  ]
}
