sys_ws_operation.operation_script:
(function process(request, response) {
    try {
        var metricsEngine = new x_1310794_founda_0.FHMetricsEngine();

        // Check if specific metrics requested via query params
        var queryParams = request.queryParams;
        var type = queryParams.type || 'comprehensive'; // Options: global, category, top_tables, trending, distribution, coverage, comprehensive

        var result = {};

        switch(type.toLowerCase()) {
            case 'global':
                result = metricsEngine.getGlobalMetrics();
                break;

            case 'category':
                result = metricsEngine.getCategoryMetrics();
                break;

            case 'top_tables':
                var limit = parseInt(queryParams.limit) || 10;
                var tables = metricsEngine.getTop10ProblematicTables();
                result = tables.slice(0, limit);
                break;

            case 'trending':
                var days = parseInt(queryParams.days) || 30;
                result = metricsEngine.getTrendingData(days);
                break;

            case 'distribution':
                result = metricsEngine.getHealthScoreDistribution();
                break;

            case 'coverage':
                result = metricsEngine.getAnalysisCoverage();
                break;

            case 'comprehensive':
            default:
                result = metricsEngine.getComprehensiveStatistics();
                break;
        }

        response.setStatus(200);
        response.setBody({
            success: true,
            type: type,
            data: result,
            timestamp: new GlideDateTime().getDisplayValue()
        });

    } catch (error) {
        gs.error('[FHA API] Error in /statistics: ' + error.message);
        response.setStatus(500);
        response.setBody({
            success: false,
            error: error.message,
            timestamp: new GlideDateTime().getDisplayValue()
        });
    }
})(request, response);

sys_ws_operation.request_example:
// GET /api/x_1310794_founda_0/fha/statistics
// 
// Query Parameters (optional):
// - type: Type of statistics to retrieve
//   Options: global, category, top_tables, trending, distribution, coverage, comprehensive (default)
// - days: Number of days for trending data (default: 30)
// - limit: Limit for top tables (default: 10)
//
// Examples:
