x_1310794_founda_0_issue_rules.script:
if (item.values.operation && item.values.admin_overrides !== 'true') {
    var type = item.values.type || '';
    var script = item.values.script || '';

    // Vérifications
    var hasRole = item.values.roles && item.values.roles.length > 0;
    var hasCondition = item.values.condition && item.values.condition.length > 10;
    var hasScript = script.length > 10;

    // ACL sans protection
    if (!hasRole && !hasCondition && !hasScript) {
        var recordName = item.values.name || 'ACL';
        var message = 'ACL "' + recordName + '" n\'a aucune restriction (pas de rôle, condition ou script). Cela donne accès à tout le monde.';

        issues.push({
            code: rule.code,
            message: message,
            severity: 'high',
            details: {
                operation: item.values.operation,
                type: type,
                record_table: item.table,
                record_sys_id: item.sys_id,
                record_name: recordName,
                recommendation: 'Ajouter des exigences de rôle, des conditions ou des restrictions de script'
            }
        });
    }

    // ACL avec script "return true"
    if (hasScript && /return\s+true\s*;?\s*$/.test(script.trim())) {
        var recordName = item.values.name || 'ACL';
        var message = 'ACL "' + recordName + '" le script retourne toujours true. Supprimer ou ajouter de la logique.';

        issues.push({
            code: rule.code,
            message: message,
            severity: 'high',
            details: {
                script_preview: script.substring(0, 100),
                record_table: item.table,
                record_sys_id: item.sys_id,
                record_name: recordName,
                recommendation: 'Ajouter une logique de contrôle d\'accès appropriée'
            }
        });
    }
}
