x_1310794_founda_0_issue_rules.script:
if (item.values.script) {
    var script = item.values.script.toString();
    var lineCount = (script.match(/\n/g) || []).length + 1;
    var charCount = script.length;
    var maxLines = params.max_lines || 100;
    var maxChars = params.max_chars || 2000;

    if (lineCount > maxLines || charCount > maxChars) {
        var recordName = item.values.name || 'Business Rule';
        var message = 'Business Rule "' + recordName + '" trop complexe: ' + lineCount + ' lignes, ' + charCount + ' caractères. Envisagez de refactoriser dans un Script Include.';

        issues.push({
            code: rule.code,
            message: message,
            severity: lineCount > 200 ? 'high' : (rule.severity || 'medium'),
            details: {
                line_count: lineCount,
                char_count: charCount,
                max_lines: maxLines,
                max_chars: maxChars,
                record_table: item.table,
                record_sys_id: item.sys_id,
                record_name: recordName,
                recommendation: 'Décomposer en fonctions plus petites dans un Script Include'
            }
        });
    }
}
