x_1310794_founda_0_issue_rules.script:
if (item.values.script) {
    var script = item.values.script.toString();
    var lineCount = (script.match(/\n/g) || []).length + 1;
    var maxLines = params.max_lines || 50;

    // Recherche d'anti-patterns
    var hasGlideAjax = script.indexOf('GlideAjax') !== -1;
    var hasLoops = /for\s*\(|while\s*\(/.test(script);
    var hasDOMManip = /document\.|getElementById|querySelector/.test(script);

    var issues_found = [];
    if (lineCount > maxLines) issues_found.push(lineCount + ' lignes');
    if (hasGlideAjax) issues_found.push('appel GlideAjax');
    if (hasLoops) issues_found.push('boucles');
    if (hasDOMManip) issues_found.push('manipulation DOM');

    if (issues_found.length > 0) {
        var recordName = item.values.name || 'Client Script';
        var message = 'Client Script "' + recordName + '" peut impacter les performances: ' + issues_found.join(', ') + '.';

        issues.push({
            code: rule.code,
            message: message,
            severity: issues_found.length > 2 ? 'high' : (rule.severity || 'medium'),
            details: {
                line_count: lineCount,
                max_lines: maxLines,
                has_ajax: hasGlideAjax,
                has_loops: hasLoops,
                has_dom_manip: hasDOMManip,
                record_table: item.table,
                record_sys_id: item.sys_id,
                record_name: recordName,
                recommendation: 'Optimiser en réduisant la complexité et en évitant les opérations synchrones'
            }
        });
    }
}
