x_1310794_founda_0_issue_rules.script:
var keyFields = params.key_fields || 'name';
var fields = keyFields.split(',').map(function(f){ return (f || '').trim(); });

var keyParts = [];
fields.forEach(function(f) {
    keyParts.push(item.values[f] || '');
});
var key = keyParts.join('|');

var seen = context && context._dupsSeen;
if (!seen) return issues;

if (seen[key] && seen[key] !== item.sys_id) {
    var recordName = item.values.name || item.values.title || 'Record';
    var message = 'Doublon détecté: "' + recordName + '" a les mêmes ' + fields.join(', ') + ' qu\'un autre enregistrement. Cela peut causer des conflits.';

    issues.push({
        code: rule.code,
        message: message,
        severity: rule.severity || 'medium',
        details: { 
            duplicate_of: seen[key],
            key_fields: fields,
            key_value: key,
            record_table: item.table,
            record_sys_id: item.sys_id,
            record_name: recordName,
            recommendation: 'Renommer ou fusionner avec l\'enregistrement dupliqué'
        }
    });
}
