x_1310794_founda_0_issue_rules.script:
var fields = (params.field || params.fields || '').split(',');
var recordName = item.values.name || item.values.title || 'Record';

fields.forEach(function(fRaw) {
    var f = (fRaw || '').trim();
    if (!f) return;
    var v = item.values[f];
    if (v === undefined || v === null || v === '') {
        var message = 'Champ requis manquant "' + f + '" dans "' + recordName + '". Cela peut causer des problèmes ou une configuration incomplète.';
        issues.push({
            code: rule.code,
            message: message,
            severity: rule.severity || 'medium',
            details: { 
                field: f,
                record_table: item.table,
                record_sys_id: item.sys_id,
                record_name: recordName,
                recommendation: 'Remplir le champ ' + f + ' pour compléter la configuration'
            }
        });
    }
});
