x_1310794_founda_0_issue_rules.script:
var field = params.field || 'text';
var maxLen = params.max_len || 0;
var val = (item.values[field] || '').toString();
var recordName = item.values.name || item.values.title || 'Record';

if (maxLen && val.length > maxLen) {
    var percentage = Math.round((val.length / maxLen) * 100);
    var message = 'Champ "' + field + '" trop long dans "' + recordName + '": ' + val.length + ' caractères (limite: ' + maxLen + ', ' + percentage + '%). Envisagez de refactoriser ou diviser.';

    issues.push({
        code: rule.code,
        message: message,
        severity: rule.severity || 'medium',
        details: { 
            field: field, 
            length: val.length,
            max_length: maxLen,
            percentage: percentage,
            record_table: item.table,
            record_sys_id: item.sys_id,
            record_name: recordName,
            recommendation: 'Décomposer le ' + field + ' volumineux en fonctions ou modules plus petits'
        }
    });
}
