sp_widget.client_script:
api.controller=function($scope, $location, $timeout, $window) {
  var c = this;

  // Active section for navigation
  c.activeSection = 'overview';
  var sectionIds = [
    'overview',
    'quickstart', 
    'components',
    'configuration',
    'architecture',
    'api',
    'handlers',
    'troubleshooting',
    'bestpractices',
    'documentation'
  ];
  var observer = null;

  // Data
  c.version = c.data.appVersion || '1.3.0';
  c.lastUpdated = c.data.lastUpdated || '2026-01-17';
  c.components = c.data.components || {};

  // Set active section
  c.setSection = function(section) {
    c.activeSection = section;
  };

  // Scroll to section with smooth behavior
  c.scrollToSection = function(sectionId) {
    c.activeSection = sectionId;
    $timeout(function() {
      var element = document.getElementById(sectionId);
      if (element) {
        var headerOffset = 80;
        var elementPosition = element.getBoundingClientRect().top;
        var offsetPosition = elementPosition + window.pageYOffset - headerOffset;

        window.scrollTo({
          top: offsetPosition,
          behavior: 'smooth'
        });
      }
    }, 100);
  };

  // Scroll spy - track active section based on viewport
  function setActiveFromViewport(entries) {
    entries.forEach(function(entry) {
      if (entry.isIntersecting && entry.target && entry.target.id) {
        c.activeSection = entry.target.id;
        $scope.$applyAsync();
      }
    });
  }

  function initScrollSpy() {
    if (!('IntersectionObserver' in window)) {
      return;
    }
    if (observer) {
      observer.disconnect();
    }
    observer = new IntersectionObserver(setActiveFromViewport, { 
      threshold: 0.3,
      rootMargin: '-80px 0px -60% 0px'
    });
    sectionIds.forEach(function(id) {
      var el = document.getElementById(id);
      if (el) {
        observer.observe(el);
      }
    });
  }

  $timeout(initScrollSpy, 300);

  $scope.$on('$destroy', function() {
    if (observer) {
      observer.disconnect();
    }
  });

  // Navigation
  c.goToDashboard = function() {
    $window.location.href = '/fha?id=fha_homepage';
  };

  c.openPortal = function(pageId) {
    $window.location.href = '/fha?id=' + pageId;
  };

  // Copy to clipboard
  c.copyToClipboard = function(text) {
    if (navigator.clipboard) {
      navigator.clipboard.writeText(text).then(function() {
        // Show success feedback
        c.showCopyFeedback = true;
        $timeout(function() {
          c.showCopyFeedback = false;
        }, 2000);
      });
    }
  };
};

